import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ModalController } from '@ionic/angular';
import { RendezVousData, getPatientsByMedUid, getConsultationsForPatient, medecinUid } from 'src/firebaseConfig';
import { FichePatientModalComponent } from './fiche-patient-modal/fiche-patient-modal.component';
import { ConsultationsModalComponent } from './consultations-modal/consultations-modal.component';
import { ConsultationslisteModalComponent } from '../patients/consultationsliste-modal/consultationsliste-modal.component';
import { Router } from '@angular/router';

interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}

interface PatientData {
  nom: string;
  prenom: string;
  age?: number;
  adresse?: string;
  telephones?: string;
  uid: string;
  medecinUid?: string; 
}

@Component({
  selector: 'app-affiche-patient',
  templateUrl: './patient.page.html',
  styleUrls: ['./patient.page.scss'],
})
export class PatientPage implements OnInit {
  patients: PatientData[] = [];
  rdvs: RendezVousData[] = [];
  uid: string = medecinUid.uid;
  nomPatient: string = ''; 
  prenomPatient: string = '';
  patientUid: string = ''; // Ajout de la variable patientUid pour l'UID du patient



  constructor(private firestore: AngularFirestore, private modalController: ModalController, private router: Router) { }

  ngOnInit() {
    this.getPatients();

  }

  async gotopatientpage(patient:PatientData){
  patient=patient
  this.router.navigate(['/patientpage']);
}
  async getPatients() {
    try {
      this.patients = await getPatientsByMedUid(this.firestore, this.uid);
      if (this.patients.length > 0) {
        this.nomPatient = this.patients[0].nom;
        this.prenomPatient = this.patients[0].prenom;
      } 
      console.log("Patients trouvés :", this.patients);
    } catch (error) {
      console.error('Erreur lors de la récupération des patients :', error);
    }
  }

  async openFichePatientModal(patient: PatientData) {
    const modal = await this.modalController.create({
      component: FichePatientModalComponent,
      componentProps: {
        patientData: patient,
        medecinUid: this.uid // Passer l'UID du médecin ici
      }
    });

    modal.onDidDismiss().then((data) => {
      if (data && data.data) {
        console.log('Données de la fiche patient à enregistrer :', data.data);
      }
    });


    return await modal.present();
  }

  async showConsultations(uid :string) {
    patientUidA=uid;
    this.router.navigate(['/consultationliste']);

  }

med=this.uid;
n=this.nomPatient ;
p=this.prenomPatient;



}
export let med :string ;
export let n :string ;
export let p :string ;
export let patientUidA: string ; // Exporter la variable patientUid
export let patient:PatientData;

<ion-header>
  <ion-toolbar>
    <ion-title>Liste des Patients</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <ion-grid>
      <ion-row>
        <ion-col ><strong>Option</strong></ion-col>
        <ion-col ><strong>Nom</strong></ion-col>
        <ion-col ><strong>Prénom</strong></ion-col>
        <ion-col ><strong>Age</strong></ion-col>
        <ion-col ><strong>Adresse</strong></ion-col>
        <ion-col ><strong>Téléphone</strong></ion-col>
      </ion-row>
      <ion-row *ngFor="let patient of patients">
        <ion-col >
          <ion-button (click)="gotopatientpage(patient)" color="primary">Fiche</ion-button>
        </ion-col>
        <ion-col >{{ patient.nom }}</ion-col>
        <ion-col >{{ patient.prenom }}</ion-col>
        <ion-col >{{ patient.age }}</ion-col>
        <ion-col >{{ patient.adresse }}</ion-col>
        <ion-col >{{ patient.telephones }}</ion-col>

      </ion-row>
    </ion-grid>
  </ion-list>
</ion-content>
