import { CommonModule } from '@angular/common';
import { Component, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { IonicModule, ModalController } from '@ionic/angular';
import { userUid } from 'src/app/login/login.page';
import { getPersonByUid } from 'src/firebaseConfig';
import { WorkHoursModalComponent } from '../work-hours-modal/work-hours-modal.component';
import { AjoutPersonneModalComponent } from '../ajout-personne-modal/ajout-personne-modal.component';

@Component({
  selector: 'app-personne-modal',
  templateUrl: './personne-modal.component.html',
  styleUrls: ['./personne-modal.component.scss'],
})
export class PersonneModalComponent  implements OnInit {

  pers : string='';

  constructor(private firestore: AngularFirestore, private modalController: ModalController) { }

  ngOnInit() {
    this.fetchPersonne();
  }


  people: any[] = [];


  async fetchPersonne() {
    try {
      const uid = "D1UoD4reN0QvhgGDFnarodjbE782"; // Remplacez 'votre_uid_a_rechercher' par l'UID que vous souhaitez rechercher
      const people = await getPersonByUid(this.firestore, uid);
      console.log('Personne trouvée :', people);
      // Utilisez les données de la personne récupérée ici
    } catch (error) {
      console.error('Erreur lors de la récupération de la personne par UID :', error);
      // Gérez l'erreur selon vos besoins
    }
  }

  selectedPeoplenom: string='';
  selectedPeopleprenom: string='';

  toggleSelection(person : any) {
    this.selectedPeoplenom = person.nom;
    this.selectedPeopleprenom = person.prenom;
  }

  close() {
    this.modalController.dismiss();
  }

  async openWorkHoursModal() {
    const modal = await this.modalController.create({
      component: WorkHoursModalComponent,
      cssClass: 'work-hours-modal',
    });
    return await modal.present();
  }

  async ajoutPersonneModal(){
    const modal = await this.modalController.create({
      component: AjoutPersonneModalComponent,
      cssClass: 'work-hours-modal',
    });
    return await modal.present();
  }

}

@NgModule({
  declarations: [PersonneModalComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule
  ],
  providers: [AngularFirestore],
})
export class PersonneModalModule {}

<ion-header>
  <ion-toolbar>
    <ion-title>Personne</ion-title>
    <ion-buttons slot= "end">
      <ion-button (click)="openWorkHoursModal()">Suivant</ion-button>
    </ion-buttons>
    <ion-buttons slot="start">
      <ion-button (click)="close()">Fermer</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>


<label>
<input type="checkbox">
Vous
</label>
<div *ngFor="let person of people">
  <label>
    <input type="checkbox" [value]="person.uid" (change)="toggleSelection(person)">
    {{ person[0].nom }} {{ person[0].prenom }}
  </label>
</div>

<ion-button (click)="ajoutPersonneModal()">Ajouter une personne</ion-button>
