import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { Router } from '@angular/router';
import { ToastController } from '@ionic/angular';
import { medecinUid } from '../recherche-medecin/recherche-medecin.page';
import { loginsecretaire,secuid } from 'src/firebaseConfig';

@Component({
  selector: 'app-loginsecretaire',
  templateUrl: './loginsecretaire.page.html',
  styleUrls: ['./loginsecretaire.page.scss'],
})
export class LoginsecretairePage implements OnInit {

  email: string = '';
  password: string = '';
  navCtrl: any;


  constructor(private toastController: ToastController, private firestore: AngularFirestore, private router: Router) { }
  ngOnInit(){

  }


  async onLoginSecretaire() {
    try {
      const result = await loginsecretaire(this.firestore, this.email, this.password);
      if (result) {
        const { uid } = result;
        console.log('Login successful! Navigating to homemedecin page...');
        console.log('Medecin UID:', uid);

        // Affectez la valeur de l'UID à la variable exportable medecinUid
        secuid.uid = uid;


        this.router.navigate(['/app-secretaire']);
      } else {
        console.log('Login failed. Handle failure actions.');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion du médecin :', error);
    }
  }

  back() {
    this.navCtrl.navigateForward('/home');
  }
}

  <ion-content>

    <ion-grid>
      <ion-row >
        <ion-col >
          <div class="ion-text-center">
            <br><br><br>
            <h4 class="warning">Connexion Secrétaire</h4>
          </div>
          <br><br><br><br><br><br>
          <div class="form">
            <ion-item>
              <ion-input placeholder="Entrer votre email" type="email" [(ngModel)]="email">

                <ion-icon name="mail-outline">
                </ion-icon>
              </ion-input>
            </ion-item>
            <br>
            <form>
            <ion-item>
              <ion-input placeholder="Entrer votre mot de passe" type="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}">

                <ion-icon name="lock-closed-outline"></ion-icon>
              </ion-input>
            </ion-item>
          </form>
            <br>
          </div>
          <div>
            <ion-button (click)="onLoginSecretaire()" size="large" expand="block" shape="round" color="success">Connexion</ion-button>
            <ion-button routerLink="/forgotpass" fill="clear" class="danger">
              Mot de passe oublié ?
            </ion-button>

          </div>
        </ion-col>
      </ion-row>
    </ion-grid>
  </ion-content>
