import { Component, NgModule, OnInit } from '@angular/core';
import { IonicModule, ModalController } from '@ionic/angular';
import { ConsultationsModalComponent } from '../consultations-modal/consultations-modal.component';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AngularFirestore } from '@angular/fire/compat/firestore';

@Component({
  selector: 'app-consultationsliste-modal',
  templateUrl: './consultationsliste-modal.component.html',
  styleUrls: ['./consultationsliste-modal.component.scss'],
})
export class ConsultationslisteModalComponent implements OnInit {
  consultations: any[] = []; // Remplacer any[] par le type de données des consultations

  constructor(private modalController: ModalController) { }

  ngOnInit() {}

  async openConsultationModal(consultation: any) {
    // Ouvrir le modal de consultation pour modifier les détails de la consultation
    const modal = await this.modalController.create({
      component: ConsultationsModalComponent, // Remplacer VOTRE_COMPONENT_MODAL par le composant modal de consultation
      componentProps: {
        consultationData: consultation
      }
    });
    await modal.present();
  }

  closeModal() {
    // Fermer le modal de la liste des consultations
    this.modalController.dismiss();
  }
}
@NgModule({
  declarations: [ConsultationslisteModalComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule
  ],
  providers: [AngularFirestore],
})
export class ConsultationslisteModalModule {}
<ion-header>
  <ion-toolbar>
    <ion-title>Liste des consultations</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="closeModal()">Fermer</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <ion-item *ngFor="let consultation of consultations">
      <ion-label>{{ consultation.date }}</ion-label>
      <ion-button (click)="openConsultationModal(consultation)" color="primary">Modifier</ion-button>
    </ion-item>
  </ion-list>
</ion-content>
