import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getMedecinByUid, getMedecinByUidA, medecinUid } from 'src/firebaseConfig'; // Importez votre fonction getMedecinByUid

interface MedecinData {
  uid:string,
  nom: string;
  prenom: string;
  sexe: string;
  email: string;
  password: string;
  date: string;
  delegation: string;
  etat: string;
  specialite: string;
  telephone: string;
}

@Component({
  selector: 'app-parametre-med-profil',
  templateUrl: './parametre-med-profil.page.html',
  styleUrls: ['./parametre-med-profil.page.scss'],
})
export class ParametreMedProfilPage implements OnInit {
  uid=medecinUid.uid;
  nom:string="";
  prenom:string="";
  sexe:string="";
  email: string="";
  password: string="";
  date: string="";
  delegation: string="";
  etat: string="";
  specialite: string="";
  telephone: string="";

  medecin: MedecinData| null = null; // Initialisez medecin à null
  formulaireActif: boolean = false; // Indicateur pour activer/désactiver le formulaire
 ; // Remplacez ceci par l'UID du médecin que vous souhaitez afficher

  constructor(private router: Router, private firestore: AngularFirestore) {}

  ngOnInit() {
    if(this.medecin){
    this.fetchMedecin();
    }
  }

  async fetchMedecin() {
    try {
      this.medecin = await getMedecinByUidA(this.firestore, medecinUid.uid);
      if (this.medecin) {
        console.log('Médecin récupéré :', this.medecin);
        // Mettez à jour les champs du formulaire avec les données récupérées du médecin
        this.nom = this.medecin.nom;
        this.prenom = this.medecin.prenom;
        this.sexe = this.medecin.sexe;
        this.email = this.medecin.email;
        this.password = this.medecin.password;
        this.date = this.medecin.date;
        this.delegation = this.medecin.delegation;
        this.etat = this.medecin.etat;
        this.specialite = this.medecin.specialite;
        this.telephone = this.medecin.telephone;
      } else {
        console.error('Aucun médecin trouvé pour cet UID :', medecinUid.uid);
      }
    } catch (error) {
      console.error('Erreur lors de la récupération du médecin :', error);
    }
  }

  activerFormulaire() {
    this.formulaireActif = true; // Active le formulaire pour les modifications
  }

  async enregistrerDonnees(formulaire: any) {
    try {
      // Vérifiez que medecin n'est pas null avant de tenter la mise à jour
      if (this.medecin) {
        const medecinA = { ...this.medecin, ...formulaire };
        await this.firestore.collection('medecin').doc(this.uid).update(medecinA);
        console.log('Données du médecin mises à jour avec succès dans Firebase.');
        this.formulaireActif = false; // Désactivez le formulaire après la mise à jour
      } else {
        console.error('Aucun médecin trouvé pour la mise à jour.');
      }
    } catch (error) {
      console.error('Erreur lors de la mise à jour des données du médecin dans Firebase :', error);
    }
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-button routerLink="/parametre-med" fill="clear" slot="start"> Retour</ion-button>
    <ion-title>Paramètre du profil du médecin</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Paramètre du profil du médecin</ion-title>
    </ion-toolbar>
  </ion-header>


      <h3>Paramètres du médecin</h3> 

      <form #medecinForm="ngForm" >
        <ion-item>
          <ion-label position="floating">Nom</ion-label>
          <ion-input type="text" [(ngModel)]="nom" name="nom" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Prénom</ion-label>
          <ion-input type="text" [(ngModel)]="prenom" name="prenom" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Email</ion-label>
          <ion-input type="email" [(ngModel)]="email" name="email" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Mot de passe</ion-label>
          <ion-input type="password" [(ngModel)]="password" name="password" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Date de naissance</ion-label>
          <ion-input type="date" [(ngModel)]="date" name="date" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Sexe</ion-label>
          <ion-select [(ngModel)]="sexe" name="sexe" [disabled]="!formulaireActif">
            <ion-select-option value="homme">Homme</ion-select-option>
            <ion-select-option value="femme">Femme</ion-select-option>
          </ion-select>
        </ion-item>

        <ion-item>
          <ion-label position="floating">Spécialité</ion-label>
          <ion-input type="text" [(ngModel)]="specialite" name="specialite" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Téléphone</ion-label>
          <ion-input type="tel" [(ngModel)]="telephone" name="telephone" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Delegation</ion-label>
          <ion-input type="text" [(ngModel)]="delegation" name="delegation" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Etat</ion-label>
          <ion-input type="text" [(ngModel)]="etat" name="etat" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-row>
          <ion-col>
            <ion-button expand="block" color="primary" (click)="activerFormulaire()">Modifier</ion-button>
          </ion-col>
          <ion-col>
            <ion-button expand="block" color="success" type="submit" [disabled]="!formulaireActif" (click)="enregistrerDonnees(medecinForm)">Enregistrer</ion-button>
          </ion-col>
        </ion-row>
      </form>


</ion-content>
