import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-imc',
  templateUrl: './imc.page.html',
  styleUrls: ['./imc.page.scss'],
})
export class ImcPage implements OnInit {
  taille: number = 0;
  poidsInput: string = '';
  imc: number | null = null;
  poids: number = 0;


  constructor() { }

  ngOnInit() { }

  calculer() {
    this.poids = parseFloat(this.poidsInput);
    this.imc = this.poids / Math.pow((this.taille / 100), 2);
  }
}
