import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ModalController } from '@ionic/angular';
import { RendezVousData, getPatientsByMedUid, getConsultationsForPatient, medecinUid } from 'src/firebaseConfig';
import { FichePatientModalComponent } from './fiche-patient-modal/fiche-patient-modal.component';
import { ConsultationsModalComponent } from './consultations-modal/consultations-modal.component';
import { ConsultationslisteModalComponent } from './consultationsliste-modal/consultationsliste-modal.component';

interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}

interface PatientData {

  nom: string;
  prenom: string;
  age?: number;
  adresse?: string;
  telephones?: string;
  uid?: string;
}

@Component({
  selector: 'app-affiche-patient',
  templateUrl: './patients.page.html',
  styleUrls: ['./patients.page.scss'],
})
export class PatientsPage implements OnInit {
  patients: PatientData[] = [];
  rdvs: RendezVousData[] = [];
  uid: string = medecinUid.uid;

  constructor(private firestore: AngularFirestore, private modalController: ModalController) { }

  ngOnInit() {
  }
}
