import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home-principal',
  templateUrl: './home-principal.page.html',
  styleUrls: ['./home-principal.page.scss'],
})
export class HomePrincipalPage implements OnInit {

  constructor() { }

  ngOnInit() {
  }

}
<ion-content [fullscreen]="true" class="centrer-contenu">
  <br><br>
  <div class="backgrounds">
  <img src="../../assets/image/shifai.png" class="circle">
  <h1 class="primary">Shifai</h1>
  <br><br>
  <p>Votre sante entre vos mains</p>
  <br><br>
  <ion-button routerLink="/homeindividu">Découvrez nos services</ion-button>
  <br><br>  <br><br>
  <p class="white">Si vous étes un medecin </p>
  <ion-button id="orange" routerLink="/homemedecin">Version Pro</ion-button>
</div>
</ion-content>
