import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { Router } from '@angular/router';
import { getPatientsByMedecinUid } from 'src/firebaseConfig';

@Component({
  selector: 'app-patient',
  templateUrl: './patient.page.html',
  styleUrls: ['./patient.page.scss'],
})
export class PatientPage implements OnInit {

  patients: string[] = []; 
  patientUid :string = "";

  constructor(private router: Router, private firestore: AngularFirestore) { }

  ngOnInit() {

    const medecinUid = 'b2xIfQRkJABI4OL9JehI';

    // Call the function to get patients
    this.getPatientsByMedecinUid(medecinUid);
  }

  redirectToPatientPage(uid: string) {

    this.patientUid = uid;

    this.router.navigate(['/affiche-patient']);
  }

  async getPatientsByMedecinUid(medecinUid: string) {
    try {
      const patients = await getPatientsByMedecinUid(this.firestore, medecinUid);

      // Now you can use the 'patients' array as needed
      console.log('Patients:', patients);
    } catch (error) {
      console.error('Error fetching patients:', error);
      // Handle the error as needed
    }
  }
}



<ion-content [fullscreen]="true">
  <ion-header >
    <ion-toolbar>
      <ion-button  slot="start" routerLink="/app-medecin" fill="clear">
        ← Retour
      </ion-button>
      <ion-title>Patient</ion-title>
      <ion-header collapse="condense">

      </ion-header>
    </ion-toolbar>
  </ion-header>
  <!-- patient-list.component.html -->

<!-- patient-list.component.html -->

<table>
  <thead>
    <tr>
      <th>Nom</th>
      <th>Prénom</th>
      <th>Email</th>
      <th>Téléphone</th>
      <th>Date de naissance</th>
      <th>Sexe</th>
      <th>Action</th>
    </tr>
  </thead>
  <tbody>
    <!-- Utilisez *ngFor pour parcourir la liste des patients -->
    <tr *ngFor="let patient of patients">
      <td>{{ patient.nom }}</td>
      <td>{{ patient.prenom }}</td>
      <td>{{ patient.email }}</td>
      <td>{{ patient.telephone }}</td>
      <td>{{ patient.date | date: 'dd/MM/yyyy' }}</td>
      <td>{{ patient.sexe }}</td>
      <td>
        <!-- Ajoutez un bouton avec un événement click -->
        <button (click)="redirectToPatientPage(patient.uid)">Voir détails</button>
      </td>
    </tr>
  </tbody>
</table>

</ion-content>
