import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getMedicamentByName } from 'src/firebaseConfig';


interface MedicamentDD {
  class: string;
  conditionnement: string;
  conservation: string;
  amm: string;
  dateamm: string;
  dci: string;
  dosage: string;
  forme: string;
  indication: string;
  laboratoire: string;
  sousclass: string;
  specialite: string;
  specification: string;
  tableau: string;
}

@Component({
  selector: 'app-recherche-medicament',
  templateUrl: './recherche-medicament.page.html',
  styleUrls: ['./recherche-medicament.page.scss'],
})
export class RechercheMedicamentPage implements OnInit {



  medicament: MedicamentDD[] = [];
  nom: string | null = null;

  constructor(private firestore: AngularFirestore) { }

  ngOnInit() {
  }

  async fetchMedicament(nomMedicament: string) {
    const medicament = await getMedicamentByName(this.firestore, nomMedicament);
    console.log('Médicament récupéré:', medicament);
    // Faites ici ce que vous voulez avec le médicament récupéré
  }

}
