import { Component, OnDestroy, ViewChild } from '@angular/core';
import { ModalController } from '@ionic/angular';
import { MedicamentFormComponent } from './medicament-form/medicament-form.component';
import { getMedicamentsByUid } from 'src/firebaseConfig';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { inscritUid } from 'src/app/login/login.page';
import { EventService } from 'src/app/event.service';
import { Subscription } from 'rxjs';
import { ModifComponent } from './modif/modif.component';


interface MedicamentData {
  idd:string; 
  nom: string;
  dosage: number;
  prise: boolean[];
  uid:string
  // Autres champs si nécessaire...
}
@Component({
  selector: 'app-medicament',
  templateUrl: 'medicament.page.html',
  styleUrls: ['medicament.page.scss']
})
export class MedicamentPage implements OnDestroy {

  @ViewChild('myModal') myModal: any;
  medicamentFormComponent = MedicamentFormComponent;

  uid = inscritUid;
  medicaments: any[] = [];
  medicament!:MedicamentData ;

  private subscription: Subscription;

  constructor(private firestore: AngularFirestore,private modalController: ModalController, private eventService: EventService) {
     // Souscrire à l'événement de mise à jour des médicaments
     this.subscription = this.eventService.medicamentUpdated.subscribe(() => {
      this.fetchMedicaments();
    });
  }



  openMedicamentFormModal() {
    this.modalController.create({
      component: this.medicamentFormComponent,
    }).then(modal => {
      modal.present();
    });
  }
  openModifModal(medID:string) {
    this.modalController.create({
      component: ModifComponent,
      componentProps :{ medID:medID }

    }).then(modal => {
      modal.present();
    });
  }

  dismissModal() {
    this.modalController.dismiss();
  }


  ionViewDidEnter() {
    // Appeler fetchMedicaments lors de l'entrée dans la vue
    this.fetchMedicaments();
  }




    // Utilisez la fonction pour récupérer les médicaments d'un utilisateur

   async fetchMedicaments() {
      try {
        this.medicaments = await getMedicamentsByUid(this.firestore, this.uid);
        console.log('Médicaments de l\'utilisateur :', this.medicaments);
        console.log(this.medicaments[0].id)
      } catch (error) {
        console.error('Erreur lors de la récupération des médicaments :', error);
      }
    }

    ngOnDestroy() {
      // Assurez-vous de désabonner la souscription pour éviter les fuites de mémoire
      this.subscription.unsubscribe();
    }



    getIconName(prises: any[], label: string): string {
      const prise = prises.find(p => p.label === label);
      return prise ? (prise.checked ? 'checkmark' : 'close') : '';
    }


}

<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-button routerLink="/tabs/tab1">Retour</ion-button>
    </ion-buttons>
    <ion-title>Mes Médicaments</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">medicament</ion-title>
    </ion-toolbar>
  </ion-header>
  <h1>Liste de Médicaments</h1>
  <ion-button class="right" (click)="openMedicamentFormModal()">Ajouter un médicament</ion-button>

  <ion-modal #myModal>
    <ion-header>
      <ion-toolbar>
        <ion-buttons slot="start">
          <ion-button (click)="dismissModal()">Cancel</ion-button>
        </ion-buttons>
        <ion-title>Ajouter un Médicament</ion-title>
      </ion-toolbar>
    </ion-header>

    <ion-content>
      <!-- Charger dynamiquement le contenu du formulaire -->
      <ng-container *ngComponentOutlet="medicamentFormComponent"></ng-container>
    </ion-content>
  </ion-modal>
<!-- Dans votre fichier HTML du composant Angular -->
<table class="table table-lg">
  <thead>
    <tr>
      <th>Nom</th>
      <th>Dosage</th>
      <th>Prises</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <!-- Utilisez *ngFor pour itérer sur le tableau de médicaments -->
    <tr *ngFor="let medicament of medicaments">
      <td>{{ medicament.nom }}</td>
      <td>{{ medicament.dosage }}</td>
      <td>
        <ng-container *ngFor="let prise of medicament.prises">
          <!-- Affichez l'étiquette de la prise seulement si checked est true -->
          <ng-container *ngIf="prise.checked">
            {{ prise.label }}
          </ng-container>
        </ng-container>
      </td>
      <td>
        <!-- Ajoutez des boutons pour la modification et la suppression -->
        <button (click)="openModifModal(medicament.idd)">Modifier/supprimer</button>
      </td>
    </tr>
  </tbody>
</table>



</ion-content>
