import { CommonModule } from '@angular/common';
import { Component, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { IonicModule, ModalController } from '@ionic/angular';

@Component({
  selector: 'app-excuse-modal',
  templateUrl: 'excusemodal.component.html',
  styleUrls: ['./excusemodal.component.scss'],
})
export class ExcuseModalComponent implements OnInit {
  predefinedExcuses: string[] = [
    'Urgence personnelle',
    'Conflit d\'horaire',
    'Maladie inattendue',
    'Autre'
  ];
  selectedExcuse: string = '';

  constructor(private modalController: ModalController) {}

  ngOnInit() {}

  dismiss() {
    this.modalController.dismiss();
  }

  confirm() {
    if (this.selectedExcuse) {
      console.log('Excuse sélectionnée :', this.selectedExcuse);
      this.modalController.dismiss(this.selectedExcuse);
    } else {
      console.log('Aucune excuse sélectionnée.');
    }
  }
}
@NgModule({
  declarations: [ExcuseModalComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule
  ],
  providers: [AngularFirestore],
})
export class excusemodalModule {}
<ion-header>
  <ion-toolbar>
    <ion-title>Sélectionnez votre excuse</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <ion-item>
      <ion-label>Excuse</ion-label>
      <ion-select [(ngModel)]="selectedExcuse">
        <ion-select-option *ngFor="let excuse of predefinedExcuses" [value]="excuse">{{ excuse }}</ion-select-option>
      </ion-select>
    </ion-item>
  </ion-list>
</ion-content>

<ion-footer>
  <ion-toolbar>
    <ion-button expand="full" color="light" (click)="dismiss()">Annuler</ion-button>
    <ion-button expand="full" color="primary" (click)="confirm()">Confirmer</ion-button>
  </ion-toolbar>
</ion-footer>
