import { Component, OnInit } from '@angular/core';
import { medecinUid, modifierSauvegarderConsultation } from 'src/firebaseConfig';
import { patientUidA } from '../patient/patient.page';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { consultationdate } from '../consultationliste/consultationliste.page';




interface PatientData {
  id: string;
  nom: string;
  prenom: string;
  age?: number;
  adresse?: string;
  telephones?: string;
  uid?: string;
  medecinUid?: string; // Ajout de medecinUid dans PatientData
}
interface consultationData {
  date: string;
  details?: string;
}

@Component({
  selector: 'app-consultation',
  templateUrl: './consultation.page.html',
  styleUrls: ['./consultation.page.scss'],
})
export class ConsultationPage implements OnInit {
  date: string = consultationdate; // Ajoutez la propriété date
  newDetails: string = ''; // Ajoutez la propriété newDetails

  constructor(private firestore: AngularFirestore) { }

  ngOnInit() {
  }
  async modifierConsultation(date: string, newDetails: string) {
    try {
      await modifierSauvegarderConsultation(this.firestore, medecinUid.uid, patientUidA, date, newDetails);
      console.log('Détails de la consultation mis à jour avec succès.');
    } catch (error) {
      console.error('Erreur lors de la modification et de la sauvegarde des détails de la consultation :', error);
    }
  }

}
