import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ToastController } from '@ionic/angular';
import { getConsultationByDateAndUidPat, medecinUid } from 'src/firebaseConfig';
import { patientUidA } from '../patient/patient.page';
import { consultationdate1 } from '../patientpage/patientpage.page';

interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}

@Component({
  selector: 'app-consultationmodif',
  templateUrl: './consultation-modif.page.html',
  styleUrls: ['./consultation-modif.page.scss'],
})
export class ConsultationModifPage implements OnInit {
consultation=consultationdate1;

  constructor(
    private route: ActivatedRoute,
    private firestore: AngularFirestore,
    private router: Router,
    private toastController: ToastController
  ) {}

  ngOnInit() {

  }

  async updateConsultation(consultation: consultationData) {
    try {
      // Récupérer la référence du document de consultation en fonction de la date
      const querySnapshot = await this.firestore.collection('medecin').doc(medecinUid.uid)
        .collection('patients').doc(patientUidA).collection('consultations')
        .ref.where('date', '==', consultation.date).get();

      // Vérifier s'il y a des résultats
      if (!querySnapshot.empty) {
        // Mettre à jour les détails de la première consultation correspondant à la date
        const consultationDoc = querySnapshot.docs[0]; // Prendre le premier document trouvé
        await consultationDoc.ref.update({
          date: consultation.date,
          details: consultation.details,
        });

        // Rediriger vers la page de consultation
        this.router.navigate(['/patientpage']);
      } else {
        console.error('Aucune consultation trouvée pour cette date.');

        // Afficher un message d'erreur
        const toast = await this.toastController.create({
          message: 'Aucune consultation trouvée pour cette date.',
          duration: 1500,
          position: 'top',
          color: 'danger',
        });
        await toast.present();
      }
    } catch (error) {
      console.error('Erreur lors de la modification de la consultation :', error);

      // Afficher un message d'erreur
      const toast = await this.toastController.create({
        message: 'Erreur lors de la modification de la consultation.',
        duration: 1500,
        position: 'top',
        color: 'danger',
      });
      await toast.present();
    }
}


  async loadConsultation() {
    try {
      const consultationSnapshot = await this.firestore.collection('medecin').doc(medecinUid.uid).collection('patients').doc(patientUidA)
      .collection('consultations')
      .ref.where('date', '==', consultationdate1.date).get();
