import { Component, NgModule, OnInit } from '@angular/core';
import { IonicModule, ModalController } from '@ionic/angular';
import { RendezvousmodalComponent } from '../rendezvousmodal/rendezvousmodal.component';
import { medecinUid } from 'src/app/recherche-medecin/recherche-medecin.page';
import { selectedDate } from '../work-hours-modal/work-hours-modal.component';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AngularFirestore } from '@angular/fire/compat/firestore';

@Component({
  selector: 'app-heure',
  templateUrl: './heure.component.html',
  styleUrls: ['./heure.component.scss'],
})
export class HeureComponent implements OnInit {
  selectedHour: string = '';
  workHours: string[] = [
    '8H00 - 8H30',
    '8H30 - 9H00',
    '9H00 - 9H30',
    '9H30 - 10H00',
    '10H00 - 10H30',
    '10H30 - 11H00',
    '11H00 - 11H30',
    '11H30 - 12H00',
    '14H00 - 14H30',
    '14H30 - 15H00',
    '15H00 - 15H30',
    '15H30 - 16H00',
    '16H00 - 16H30',
  ];
  hourAvailability: boolean[] = Array(this.workHours.length).fill(true); // Tableau pour indiquer l'accessibilité de chaque heure

  constructor(private modalController: ModalController, private firestore: AngularFirestore) {}

  ngOnInit() {
    console.log('Selected date:', selectedDate);
    this.getTakenHoursByDate(medecinUid, selectedDate); // Charger les heures prises au démarrage
  }

  async getTakenHoursByDate(uid: string, date: string) {
    const query = this.firestore.collection('medecin').doc(uid).collection('rdv', ref => ref.where('date', '==', date));
    const snapshot = await query.get().toPromise();

    if (snapshot) {
      this.hourAvailability = Array.from({ length: this.workHours.length }, () => true); // Reset hour availability to all available

      snapshot.docs.forEach(doc => {
        const rdvData = doc.data();
        if (rdvData && rdvData['heure']) {
          const index = this.workHours.indexOf(rdvData['heure']);
          if (index !== -1) {
            this.hourAvailability[index] = false; // Marquer l'heure comme indisponible si elle est prise
          }
        }
      });

      console.log('Hour availability:', this.hourAvailability);
    } else {
      console.error('No snapshot available.');
    }
  }
  isHourTaken(index: number): boolean {
    return !this.hourAvailability[index];
  }

  toggleSelection(index: number) {
    this.selectedHour = this.workHours[index];
    console.log('Selected date:', selectedDate);
    console.log('Selected hour:', this.selectedHour);
    console.log('Med:', medecinUid);
  }

  async openrdvModal() {
    const modal = await this.modalController.create({
      component: RendezvousmodalComponent,
      cssClass: 'work-hours-modal',
    });
    this.close();
    return await modal.present();
  }

  close() {
    this.modalController.dismiss();
  }
}

@NgModule({
  declarations: [HeureComponent],
  imports: [CommonModule, FormsModule, IonicModule],
  providers: [AngularFirestore],
})
export class HeureModule {}

export let selectedHour: string;
