import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ToastController } from '@ionic/angular';
import { getConsultationByDateAndUidPat, medecinUid,getConsultationByDate,modifierSauvegarderConsultation } from 'src/firebaseConfig';
import { patientUidA } from '../patient/patient.page';
import { consultationdate1 } from '../patientpage/patientpage.page';

interface consultationData {
  date:string;
  details: string;
  poids: string;
  tailles: string;
  temperature: string;
  frequenceCardiaque: string;
  glycemie: string;
  pressionArterielle: string;
  observation: string;
  diagnosticMedical: string;
}

@Component({
  selector: 'app-consultationmodif',
  templateUrl: './consultation-modif.page.html',
  styleUrls: ['./consultation-modif.page.scss'],
})
export class ConsultationModifPage implements OnInit {

   consultation: any ;

date:string="";
details: string="";
poids: string="";
tailles: string="";
temperature: string="";
frequenceCardiaque: string="";
glycemie: string="";
pressionArterielle: string="";
observation: string="";
diagnosticMedical: string="";

  constructor(
    private route: ActivatedRoute,
    private firestore: AngularFirestore,
    private router: Router,
    private toastController: ToastController
  ) {}

  async ngOnInit() {
    console.log(consultationdate1);
    await this.loadConsultation();
  }



  async updateConsultation() {
    try{

      await modifierSauvegarderConsultation(this.firestore, medecinUid.uid, patientUidA, consultationdate1,this.details,this.poids,this.tailles,this.temperature,this.frequenceCardiaque,this.glycemie,this.pressionArterielle,this.observation,this.diagnosticMedical);

      console.log("enregistrement reussie !!!");


    } catch (error) {
      console.error('Erreur lors de la modification de la consultation :', error);

      // Afficher un message d'erreur
      const toast = await this.toastController.create({
        message: 'Erreur lors de la modification de la consultation.',
        duration: 1500,
        position: 'top',
        color: 'danger',
      });
      await toast.present();
    }
}


  async loadConsultation() {
    try {
      const consultation = await getConsultationByDate(this.firestore, medecinUid.uid, patientUidA, consultationdate1);
      if (consultation) {
      this.consultation =consultation;

      this.date = this.consultation.date;
      console.log("date = ", this.date);
      this.details = consultation.details;
      this.poids = consultation.poids;
      this.tailles = consultation.tailles;
      this.temperature = consultation.temperature;
      this.frequenceCardiaque = consultation.frequenceCardiaque;
      this.glycemie = consultation.glycemie;
      this.pressionArterielle = consultation.pressionArterielle;
      this.observation = consultation.observation;
      this.diagnosticMedical = consultation.diagnosticMedical;
      } else {
        console.log('La consultation n\'existe pas.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement de la consultation :', error);
    }
  }
}
<ion-header >
  <ion-button routerLink="/patientpage" slot="start" fill="clear">Retour</ion-button>
  <ion-toolbar>
    <ion-title>consultation: {{ date }}</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content >

<ion-card>
    <ion-card>

    <ion-item>
      <ion-label position="floating">Motifs</ion-label>
      <ion-input [(ngModel)]="details"></ion-input>
    </ion-item>
  </ion-card>
<ion-card>
  <ion-card-header>
    <ion-card-title><b>Constante vitale</b></ion-card-title>
  </ion-card-header>
  <ion-card-content>
    <ion-item>
      <ion-label position="floating">Poids</ion-label>
      <ion-input [(ngModel)]="poids"  ></ion-input>
    </ion-item>

    <ion-item>
      <ion-label position="floating">Tailles</ion-label>
      <ion-input [(ngModel)]="tailles" ></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Température</ion-label>
      <ion-input [(ngModel)]="temperature"  ></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Fréquence cardiaque</ion-label>
      <ion-input [(ngModel)]="frequenceCardiaque"  ></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Glycémie</ion-label>
      <ion-input [(ngModel)]="glycemie"  ></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Préssion artérielle</ion-label>
      <ion-input [(ngModel)]="pressionArterielle"  ></ion-input>
    </ion-item>
  </ion-card-content>
  </ion-card>
<ion-card>
    <ion-item>
      <ion-label position="floating">Obsérvation</ion-label>
      <ion-textarea rows="6" [(ngModel)]="observation" ></ion-textarea>
    </ion-item>
  </ion-card>
  <ion-card>
    <ion-item>
      <ion-label position="floating">Diagnostic médical</ion-label>
      <ion-textarea rows="6" [(ngModel)]="diagnosticMedical"  ></ion-textarea>
    </ion-item>
  </ion-card>
    <ion-button expand="block" (click)="updateConsultation()">
      Enregistrer les modifications
    </ion-button>
  </ion-card>

</ion-content>
