import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { LoadingController, ToastController } from '@ionic/angular';
import { finalize } from 'rxjs/operators';
import { inscritUid } from '../login/login.page';
import { patientUidA } from '../patient/patient.page';
import { getDocumentsforpatient } from 'src/firebaseConfig';

interface DocumentData {
  url: string;
  description: string;
  useruid: string;
}

@Component({
  selector: 'app-document',
  templateUrl: './document.page.html',
  styleUrls: ['./document.page.scss'],
})
export class DocumentPage implements OnInit {
  documents: DocumentData[] = [];
  documentDescription: string = '';
  selectedFile: File | null = null;

  constructor(
    private firestore: AngularFirestore,
    private storage: AngularFireStorage,
    private toastController: ToastController,
    private loadingController: LoadingController
  ) {}

  ngOnInit() {
    this.getDocuments();
  }

  async getDocuments() {
    const documents = await getDocumentsforpatient(this.firestore, inscritUid);
    if (documents) {
      this.documents = documents;
    }
  }

  async onFileSelected(event: any) {
    this.selectedFile = event.target.files[0] as File;
  }

  async envoyer() {
    if (!this.selectedFile || !this.documentDescription) {
      console.error('Veuillez sélectionner un fichier et ajouter une description.');
      return;
    }

    const loading = await this.loadingController.create({
      message: 'Envoi du document en cours...',
    });
    await loading.present();

    try {
      // Générer un ID unique pour le document
      const fileId = this.firestore.createId();
      const filePath = `documents/${fileId}`;

      // Uploader le fichier vers Firebase Storage
      const fileRef = this.storage.ref(filePath);
      const uploadTask = this.storage.upload(filePath, this.selectedFile);

      // Attendre la fin du téléchargement
      await uploadTask.snapshotChanges().pipe(
        finalize(async () => {
          // Récupérer l'URL du fichier téléchargé
          const fileUrl = await fileRef.getDownloadURL().toPromise();

          // Enregistrer les métadonnées dans Firestore
          await this.firestore.collection('documents').doc(fileId).set({
            uid: fileId,
            useruid: patientUidA,
            url: fileUrl,
            description: this.documentDescription,
            // Ajoutez d'autres champs si nécessaire
          });

          // Réinitialiser les valeurs
          this.selectedFile = null;
          this.documentDescription = '';

          // Masquer le loader
          await loading.dismiss();

          // Afficher un message de succès
          const toast = await this.toastController.create({
            message: 'Le document a été enregistré avec succès.',
            duration: 1500,
            position: 'top',
            color: 'success',
          });
          await toast.present();

          // Actualiser la liste des documents
          this.getDocuments();
        })
      ).subscribe();

    } catch (error) {
      console.error('Erreur lors de l\'envoi du fichier :', error);

      // Masquer le loader en cas d'erreur
      await loading.dismiss();

      // Afficher un message d'erreur
      const toast = await this.toastController.create({
        message: 'Une erreur est survenue lors de l\'envoi du fichier.',
        duration: 1500,
        position: 'top',
        color: 'danger',
      });
      await toast.present();
    }
  }
}
