import { CommonModule } from '@angular/common';
import { Component, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { IonicModule, ModalController } from '@ionic/angular';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss'],
})
export class FormComponent  implements OnInit {

  constructor(
    private firestore: AngularFirestore,
    private modalController: ModalController,
  ) {}



  ngOnInit() {}

}
@NgModule({
  declarations: [FormComponent],
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    IonicModule,
  ],
  exports: [FormComponent],
  providers: [AngularFirestore],
})
export class FormModule {}

<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Formulaire</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="fermer()">
        <ion-icon name="close"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <form [formGroup]="myForm" >
    <!-- Champs Prénom -->
    <ion-item>

      <ion-input formControlName="prenom" label="Prénom"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="nom" label="Nom"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="numero" label="Numéro"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="gmail" label="Gmail"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="password" type="password" label="Password"></ion-input>
    </ion-item>




    <ion-button expand="full" type="submit" (click)="Modifiersecretaire()">Soumettre</ion-button>

  </form>
</ion-content>
