import { Component, OnInit } from '@angular/core';
import {  getRdvByMedUid, medecinUid } from 'src/firebaseConfig';
import { meduid } from '../login-medecin/login-medecin.page';
import { HttpClient } from '@angular/common/http';
import { AngularFirestore } from '@angular/fire/compat/firestore';
interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string;
  medecinPrenom?: string;
  medecinuid: string;
  key: string;
  excuse?:string ;
  statut?:string;
}


@Component({
  selector: 'app-salle',
  templateUrl: './salle.page.html',
  styleUrls: ['./salle.page.scss'],
})
export class SallePage implements OnInit {
uid=medecinUid.uid
rdvs:RendezVousData[]=[];
present:boolean=true;
constructor(private firestore: AngularFirestore,private http: HttpClient) { }

  ngOnInit() {
    this.loadEvents();
  }
  async loadEvents() {
    const rdvs = await getRdvByMedUid(this.firestore, this.uid);
    if (rdvs && rdvs.length > 0) {
      const today = new Date();
      this.rdvs = rdvs
        .filter(rdv => new Date(rdv.date).getDate() === today.getDate() && new Date(rdv.date).getMonth() === today.getMonth() && new Date(rdv.date).getFullYear() === today.getFullYear())
        .map(rdv => {
          this.present = false;
          return rdv;
        })
        .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
    }
  }
  }

