import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ToastController } from '@ionic/angular';
import { getRdvByMedUid, medecinUid } from 'src/firebaseConfig';

@Component({
  selector: 'app-sallesec',
  templateUrl: './sallesec.page.html',
  styleUrls: ['./sallesec.page.scss'],
})
export class SallesecPage implements OnInit {
  rdvs: any[] = [];
  p: boolean = false;
  present: boolean = false;
uid=medecinUid.uid;
  constructor(
    private firestore: AngularFirestore,
    private toastController: ToastController
  ) {}
  ngOnInit() {
    this.loadEvents();
  }

  async loadEvents() {
    const rdvs = await getRdvByMedUid(this.firestore, this.uid);
    if (rdvs && rdvs.length > 0) {
      this.p = true;
      const today = new Date();
      today.setHours(0, 0, 0, 0); // Set the time to 00:00:00 to compare only the date
      this.rdvs = rdvs
        .filter(rdv => new Date(rdv.date).setHours(0, 0, 0, 0) === today.setHours(0, 0, 0, 0))
        .map(rdv => {
          rdv.present = false;
          return rdv;
        })
        .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
      console.log(this.rdvs);
    } else {
      this.p = false;
      console.log("vous n'avez pas de rendezvous aujourd'hui");
      const toast = await this.toastController.create({
        message: 'Vous n\'avez pas de rendez-vous aujourd\'hui. Veuillez retourner avec le bouton retour.',
        duration: 1500,
        position: 'top',
        color: 'danger',
      });
      toast.present(); // Afficher le toast
    }
  }
  async updatePresent(rdv: any) {
    rdv.present = !rdv.present;
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title>Salle d'attente</ion-title>
    <ion-button routerLink="/app-secretaire" slot="start" fill="clear">retour</ion-button>

  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-grid>
    <ion-row>
      <ion-col size="6">
        <h2>Rendrez-vous du jour</h2>
      </ion-col>
    </ion-row>
    <ng-container *ngIf="p; else noAppointments">
    <ion-row *ngFor="let rdv of rdvs" [class.barre]="present">
      <ion-col size="6">
        <h3>{{ rdv.nom }} {{ rdv.prenom }} {{ rdv.heure }}</h3>
      </ion-col>
      <ion-col size="6">
        <ion-item>
          <ion-label>Présent</ion-label>
          <ion-checkbox slot="start" [(ngModel)]="rdv.present" (ionChange)="updatePresent(rdv)"></ion-checkbox>
        </ion-item>
      </ion-col>
    </ion-row>
    </ng-container>
    <ng-template #noAppointments>
      <ion-row>
        <ion-col>Vous n'avez pas de rendez-vous aujourd'hui</ion-col>
      </ion-row>
    </ng-template>
  </ion-grid>
</ion-content>
