import { Component,ChangeDetectorRef, Input, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ModalController } from '@ionic/angular';
import { getConsultations, getRdvByPatientUid, medecinUid, savePatient } from 'src/firebaseConfig';
import { patientA, patientUidA } from '../patient/patient.page';
import { meduid } from '../login-medecin/login-medecin.page';

interface PatientData {
  nom: string;
  prenom: string;
  age?: number;
  adresse?: string;
  telephones?: string;
  uid:string ;
}

interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string; // Ajout de la propriété medecinNom comme optionnelle
  medecinPrenom?: string; 
  medecinuid:string;// Ajout de la propriété medecinPrenom comme optionnelle
  key:string ;
  excuse?:string;
  statut?:string;
}
interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}
@Component({
  selector: 'app-patientpage',
  templateUrl: './patientpage.page.html',
  styleUrls: ['./patientpage.page.scss'],
})


export class PatientpagePage implements OnInit {
   rendezVousData!: RendezVousData; 

  Vfiche:boolean=true;
  Vconsultation:boolean=false;
  Vdocument:boolean=false;

  fichePatient = {
    nom: '',
    prenom: '',
    age: null,
    adresse: '',
    telephone: ''
    // Ajoutez d'autres champs selon vos besoins
  };
  rdv: RendezVousData[] | null | undefined;
  patient:PatientData =patientA ;
  consultations!: consultationData[];


  constructor(private firestore: AngularFirestore,private modalController: ModalController,private cdr: ChangeDetectorRef) {
    this.rendezVousData = {} as RendezVousData;

  }

   ngOnInit() {


    console.log('patient',patientA)
    this.loadConsultations();

    console.log('rdv',this.rdv)

  }

  async loadConsultations() {
    try {
      // Utilisez la fonction getConsultation pour obtenir les consultations
      const consultations = await getConsultations(this.firestore,medecinUid.uid, patientA.uid);
      console.log(consultations)

      if (consultations && consultations.length > 0) {
        // Assurez-vous que les consultations sont définies et non vides
        this.consultations = consultations;
      } else {
        console.log('Aucune consultation trouvée pour ce patient.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement des consultations du patient :', error);
    }
  }
  closeModal() {
    this.modalController.dismiss(); // Fermer le modal
  }

  async saveFichePatient() {
    try {
     this.fichePatient.nom=this.patient.nom ;
     this.fichePatient.prenom=this.patient.prenom;
     console.log('prenom ',this.fichePatient.prenom,'nom ',this.fichePatient.nom)

      console.log('Données de la fiche patient à sauvegarder :', this.fichePatient);

      // Appel de la fonction savePatient pour sauvegarder le patient chez le médecin
      await savePatient(this.firestore, this.fichePatient, meduid);


    } catch (error) {
      console.error('Erreur lors de l\'ajout du patient chez le médecin :', error);
    }
  }
  fiche(){
 this.Vconsultation=false;
 this.Vdocument=false;
 this.Vfiche=true;
  }

  consultation(){
    this.Vconsultation=true;
    this.Vdocument=false;
    this.Vfiche=false;
     }

     document(){
      this.Vconsultation=false;
      this.Vdocument=true;
      this.Vfiche=false;
       }


}

<ion-header>
  <ion-toolbar>
    <ion-title>info patients : </ion-title>
  </ion-toolbar>
  <ion-toolbar>
    <ion-segment >
      <ion-segment-button (click)="fiche()" >Fiche Patient</ion-segment-button>
      <ion-segment-button (click)="consultation()" >Consultations</ion-segment-button>
      <ion-segment-button (click)="document()" >Documents</ion-segment-button>
    </ion-segment>
  </ion-toolbar>
</ion-header>


  <ion-card *ngIf="Vfiche">

    <ion-card-header>
      Fiches Patients
    </ion-card-header>

    <ion-card-content>

      <ion-card>
        <p>bonjour</p>
        <ion-card-header>
          <ion-card-title>fiche patient </ion-card-title>
        </ion-card-header>

        <ion-card-content>

        <ion-card >
          <ion-card-header>
            <ion-card-title>Info patient </ion-card-title>
          </ion-card-header>
          <ion-card-content>

          <ion-item>
            <ion-label>Nom</ion-label>
            <ion-input [(ngModel)]="patient.nom" disabled></ion-input>
          </ion-item>

          <ion-item>
            <ion-label>Prénom</ion-label>
            <ion-input [(ngModel)]="patient.prenom" disabled></ion-input>
          </ion-item>

          <!-- Champs classiques d'une fiche patient -->
          <ion-item>
            <ion-label>Âge</ion-label>
            <ion-input type="number" [(ngModel)]="fichePatient.age"></ion-input>
          </ion-item>

          <ion-item>
            <ion-label>Adresse</ion-label>
            <ion-input [(ngModel)]="fichePatient.adresse"></ion-input>
          </ion-item>

          <ion-item>
            <ion-label>Téléphone</ion-label>
            <ion-input type="tel" [(ngModel)]="fichePatient.telephone"></ion-input>
          </ion-item>
        </ion-card-content>
      </ion-card>


          <ion-card >
            <ion-card-header>
              <ion-card-title>Antecedents du patient </ion-card-title>
            </ion-card-header>
            <ion-card-content>  
              <ion-button expand="block" (click)="saveFichePatient()">Enregistrer</ion-button>
            </ion-card-content>




      </ion-card>
    </ion-card-content>
  </ion-card>

  <ion-card *ngIf="Vconsultation">
    <ion-card-content>
     <ion-card>
      <ion-list>
        <ion-item *ngFor="let consultation of consultations">
          <ion-label>{{ consultation.date }}</ion-label>
          <ion-label>{{ consultation.details }}</ion-label>

          <ion-button  color="primary">Modifier</ion-button>
        </ion-item>
      </ion-list>
    </ion-card>
    </ion-card-content>
  </ion-card>


    <ion-card *ngIf="Vdocument"> 
      <ion-card-content>
            <ion-card class="upload-document">
              <ion-button fill="clear" class="upload-text">
              <ion-card-header>
                <p>
                <ion-icon name="cloud-upload-outline" large></ion-icon>
                </p>
                <ion-card-title></ion-card-title>
                <ion-card-subtitle>Upload your documents</ion-card-subtitle>
              </ion-card-header>
              <ion-card-content> </ion-card-content>
            </ion-button>
            </ion-card>
          </ion-card-content>
    </ion-card>
