import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getConsultationsmed } from 'src/firebaseConfig'; 
interface consultationData {
  date: string;
  details?: string;

}

@Component({
  selector: 'app-consultation-med',
  templateUrl: './consultation-med.page.html',
  styleUrls: ['./consultation-med.page.scss'],
})


export class ConsultationMedPage implements OnInit {
  consultations: consultationData[] = [];

  constructor(private firestore: AngularFirestore) { }

  async ngOnInit() {
    try {
      // Récupérer les consultations du médecin
      const medecinUid = 'votre_medecin_uid'; // Remplacez par l'UID de votre médecin
      this.consultations = await getConsultationsmed(this.firestore, medecinUid);
    } catch (error) {
      console.error('Erreur lors de la récupération des consultations du médecin :', error);
    }
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Consultations</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Consultations</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-list>
    <ion-item *ngFor="let consultation of consultations">
      <!-- Affichez les détails de chaque consultation ici -->
      Date : {{ consultation.date }} <br>
      Details : {{ consultation.details }}
    </ion-item>
  </ion-list>
</ion-content>
