import { ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { LoadingController, ModalController, ToastController } from '@ionic/angular';
import { finalize } from 'rxjs/operators';
import { inscritUid } from '../login/login.page';
import { getConsultations,getDocumentsIS, getDocuments, getDocumentsforpatient, getInscritByUid, getPatientByUid, medecinUid, savePatient } from 'src/firebaseConfig';
import { Router } from '@angular/router';

interface DocumentData {
  url: string;
  description: string;
  useruid: string;
}

interface PatientData {
  uid: string;
  nom: string;
  prenom: string;
  age: number;
  sexe: string;
  groupeSanguin: string;
  situationFamiliale: string;
  profession: string;
  email: string;
  telephone: string;
  telephone2: string;
  numeroAssurance: string;
  adresse: string;
  note: string;
  antecedentsMedicaux: string;
  antecedentsChirurgicaux: string;
  antecedentsFamiliaux: string;
  autresAntecedents: string;
  medicaments: string;
  useruid: string;
}

interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}

@Component({
  selector: 'app-document',
  templateUrl: './document.page.html',
  styleUrls: ['./document.page.scss'],
})
export class DocumentPage implements OnInit {
  Vfiche: boolean = true;
  Vconsultation: boolean = false;
  Vdocument: boolean = false;

    uid:string='';
    nom:string='';
    prenom:string='';
    age:number=0;
    adresse:string='';
    telephone:string='';
    sexe:string='';
    groupeSanguin:string='';
    situationFamiliale:string='';
    profession:string='';
    email:string='';
    telephone2:string='';
    numeroAssurance:string='';
    note:string='';
    antecedentsMedicaux:string='';
    antecedentsChirurgicaux:string='';
    antecedentsFamiliaux:string='';
    autresAntecedents:string='';
    medicaments:string='';
    description:string='';


  patient: PatientData | null | undefined;
  consultations!: consultationData[];
  selectedFile: File | null = null;
  selectedFileUrl: string | null = null;
  documents:DocumentData[]=[];
  consultationDate: string = ''; // Variable pour stocker la date de consultation
  consultationdate1:string='';
  searchDescription:string='';


  constructor(private storage : AngularFireStorage ,private firestore: AngularFirestore,private toastController :ToastController, private modalController: ModalController, private cdr: ChangeDetectorRef, private router: Router) {
  }

  async ngOnInit() {
    this.getdoc();


  }
  async back(){

    this.router.navigate(['/patient']);
    console.log('going to patient')
  }







  async onFileSelected(event: any) {
    const file: File | null = event.target.files[0];
    this.selectedFile = file;

    if (file instanceof File) {
      try {
        // Generate a unique ID using Firestore's createId() method
        const fileId = this.firestore.createId();

        // Construct the file path in Firebase Storage
        const filePath = `documents/${fileId}_${file.name}`;

        // Create a reference to the file in Firebase Storage
        const fileRef = this.storage.ref(filePath);

        // Upload the file to Firebase Storage
        const uploadTask = this.storage.upload(filePath, file);

        // Handle upload progress and completion
        uploadTask.snapshotChanges().pipe(
          finalize(async () => {
            try {
              // Get the downloadable URL of the uploaded file
              const fileUrl = await fileRef.getDownloadURL().toPromise();

              // Retrieve the description from the input field
              const description = this.description;

              // Validate and sanitize the description if necessary

              // Create a document data object with relevant information
              const documentData = {
                uid: fileId, // Use the generated fileId as the document ID
                medecinUid: medecinUid.uid,
                patientUid: inscritUid,
                url: fileUrl,
                description: description
              };

              // Add the document to Firestore using the docRef
              const docRef = this.firestore.collection('documents').doc(fileId);
              await docRef.set(documentData);

              // Handle successful document creation
              console.log('Document ajouté avec succès.');
              this.getdoc(); // Assuming getdocument fetches and updates documents list

            } catch (error) {
              // Handle errors during document creation
              console.error('Erreur lors de l\'ajout du document :', error);
              // Provide more specific error messages based on error.code and error.message
            }
          })
        ).subscribe();
      } catch (error) {
        // Handle errors during file upload
        console.error('Erreur lors de l\'ajout du document :', error);
        // Provide more specific error messages based on error.code and error.message
      }
    }
  }


  async envoyer() {
    if (!this.selectedFile) {
      console.error('Aucun fichier sélectionné.');
      return;
    }

    // Check if description is empty (already in your code)

    // Generate a unique ID for the file
    const fileId = this.firestore.createId();

    // Storage path in Firebase Storage
    const filePath = `documents/${fileId}`;

    try {
      // Upload the file to Firebase Storage
      const fileRef = this.storage.ref(filePath);
      const uploadTask = this.storage.upload(filePath, this.selectedFile);

      // Log upload progress (optional)
      uploadTask.snapshotChanges().pipe(
        finalize(() => console.log('Upload completed'))
      ).subscribe();

      // Wait for the upload to finish
      await uploadTask.then();

      // Get the URL of the uploaded file
      const fileUrl = await fileRef.getDownloadURL().toPromise();

      // Save metadata in Firestore
      await this.firestore.collection('documents').doc(fileId).set({
        useruid: this.patient?.useruid,
        url: fileUrl,
        description: this.description,
        // Add other fields if needed
      });

      // Update local documents array (assuming you have a method)

      console.log('Fichier téléchargé et enregistré avec succès.');
    } catch (error) {
      console.error('Erreur lors de l\'envoi du fichier :', error);
    }
  }




  closeModal() {
    this.modalController.dismiss();
  }

  async searchDocumentsByDescription(description: string) {
    try {
      if (!description) {
        console.log('Veuillez entrer une description.');
        return;
      }

      // Filtrer les documents pour la description spécifiée
      const filteredDocuments = this.documents.filter(document => document.description === description);

      if (filteredDocuments.length > 0) {
        console.log('Documents trouvés avec la description', description, ':', filteredDocuments);
        this.documents = filteredDocuments;
      } else {
        console.log('Aucun document trouvé avec la description', description);
      }
    } catch (error) {
      console.error('Erreur lors de la recherche des documents pour la description :', error);
    }
  }
  async getdoc(){
    const doc=await getDocumentsIS(this.firestore,inscritUid)
    console.log(doc)
    if(doc){
      this.documents=doc;
    }
    console.log(inscritUid)
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-button routerLink="/tabs/tab1" fill="clear">Retour</ion-button>
    </ion-buttons>
    <ion-title>Documents</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-card>
    <ion-card-content>
      <ion-card-header>
        <br>
        <ion-searchbar placeholder="Rechercher" ></ion-searchbar>
        <br>
      </ion-card-header>
          <ion-card class="upload-document">
            <ion-card-content>


              <br><br> 
              <br><br> <br><br> <br><br> 
                <input type="file" id="fileInput" name="fileInput" accept="*/*" (change)="onFileSelected($event)">

          </ion-card-content>
          </ion-card>

          <div *ngIf="selectedFile">
            <ion-input type="text" [(ngModel)]="description" placeholder="Enter document description" required></ion-input>
            <ion-button class="ion-text-end" (click)="envoyer()">
              <ion-icon name="send-outline"></ion-icon>
              &nbsp;&nbsp;Envoyer
            </ion-button>

            <img [src]="selectedFile" alt="Selected Image">
          </div>
         <ion-card>
          <div>
            <ion-list>
              <ion-input type="text" [(ngModel)]="searchDescription"placeholder="entrer une description a rechercher" color="primary" ></ion-input>
<ion-button (click)="searchDocumentsByDescription(searchDescription)">Rechercher</ion-button>
              <ion-item *ngFor="let document of documents">
                <img [src]="document.url" width="60" height="60">
                <ion-label>{{ document.description }}</ion-label>
                <a float="right" [href]="document.url"  target="_blank" > Voir détail..</a>
              </ion-item>
              <br><br>
            </ion-list>
          </div>
         </ion-card>
        </ion-card-content>
  </ion-card>

</ion-content>
