import { CommonModule } from '@angular/common';
import { Component, Input, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { IonicModule, ModalController, ToastController } from '@ionic/angular';
import { EventService } from 'src/app/event.service';
import { getMedicamentsByid, ModifierMedicament, supprimerMedicament } from 'src/firebaseConfig';

interface MedicamentData {
  idd:string;
  nom: string;
  dosage: number;
  prises: {
    label: string;
    checked: boolean;
  }[];
}

@Component({
  selector: 'app-modif',
  templateUrl: './modif.component.html',
  styleUrls: ['./modif.component.scss'],
})
export class ModifComponent implements OnInit {
  @Input() med!: MedicamentData;
  medicament!: MedicamentData;
  nom = '';
  dosage = 0;
  prises: { label: string; checked: boolean }[] = [
    { label: 'Matin', checked: false },
    { label: 'Midi', checked: false },
    { label: 'Soir', checked: false },
  ];

  constructor(
    private firestore: AngularFirestore,
    private toastController: ToastController,
    private modalController: ModalController,
    private eventService: EventService
  ) {}

  ngOnInit() {
    this.getMed();
    console.log(this.medicament.idd)
  }

  async getMed() {
    try {
      const medicament = await getMedicamentsByid(this.firestore, this.medicament.idd);
      this.medicament = medicament;
      this.nom = medicament.nom;
      this.dosage = medicament.dosage;
      this.prises = medicament.prises.map((prise) => ({ label: prise.label, checked: prise.checked }));
    } catch (error) {
      console.error('Erreur lors de la récupération du médicament:', error);
    }
  }

  async modifierMedicament() {
    try {
      await ModifierMedicament(this.firestore, this.medicament.idd, this.nom, this.dosage, this.prises);
      this.eventService.triggerMedicamentUpdated();
      await this.presentToast('Médicament modifié avec succès!');
      this.modalController.dismiss();
    } catch (error) {
      console.error('Erreur lors de la modification du médicament:', error);
    }
  }

  supprimerMedicament() {
    supprimerMedicament(this.firestore, this.medicament.idd);
  }

  async presentToast(message: string) {
    const toast = await this.toastController.create({
      message: message,
      duration: 3000,
      position: 'bottom',
    });
    toast.present();
  }

  close() {
    this.modalController.dismiss();
  }
}

@NgModule({
  declarations: [ModifComponent],
  imports: [CommonModule, FormsModule, IonicModule],
  exports: [ModifComponent],
  providers: [AngularFirestore],
})
export class ModifModule {}
