import { CommonModule } from '@angular/common';
import { Component, Input, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { IonicModule, ModalController, ToastController } from '@ionic/angular';
import { EventService } from 'src/app/event.service';
import { inscritUid } from 'src/app/login/login.page';
import { MedicamentDD, ModifierMedicament, getMedicamentsByid } from 'src/firebaseConfig';

interface MedicamentDat {
  nom: string;
  dosage: number;
  prises: {
    label: string;
    checked: boolean;
  }[];
}

@Component({
  selector: 'app-modif',
  templateUrl: './modif.component.html',
  styleUrls: ['./modif.component.scss'],
})
export class ModifComponent implements OnInit {
  @Input() medID!: string;
  medicament!: MedicamentDat;
  uid = inscritUid;
  nom = '';
  dosage = 0;
  prises = [
    { label: 'Matin', checked: false },
    { label: 'Midi', checked: false },
    { label: 'Soir', checked: false },
  ];

  constructor(
    private firestore: AngularFirestore,
    private toastController: ToastController,
    private modalController: ModalController,
    private eventService: EventService
  ) {}

  ngOnInit() {
    console.log(this.medID);
    this.getmed();
  }

  async getmed() {
    try {
      const medicament = await getMedicamentsByid(this.firestore, this.medID);
      this.medicament = medicament;
      this.nom = medicament.nom;
      this.dosage = medicament.dosage;
      this.prises = medicament.prises.map((prise) => ({
        label: prise.label,
        checked: prise.checked,
      }));
    } catch (error) {
      console.log('Erreur lors de la récupération du médicament:', error);
    }
  }

  async modifierMedicament(MedicamentID: string) {
    try {
      await ModifierMedicament(this.firestore, MedicamentID, this.nom, this.dosage, this.prises);
      this.eventService.triggerMedicamentUpdated();
      this.presentToast('Médicament modifié avec succès!');
      this.modalController.dismiss();
    } catch (error) {
      console.log('Erreur lors de la modification du médicament:', error);
    }
  }

  supprimerMedicament(id: string) {
    return this.firestore.collection('medicament').doc(id).delete();
  }

  async presentToast(message: string) {
    const toast = await this.toastController.create({
      message: message,
      duration: 3000,
      position: 'bottom',
    });
    toast.present();
  }
}

@NgModule({
  declarations: [ModifComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule,
  ],
  exports: [ModifComponent],
  providers: [AngularFirestore],
})
export class ModifModule {}
