import { Component,ChangeDetectorRef, Input, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ModalController, ToastController } from '@ionic/angular';
import { getConsultations, getFichePatient, getRdvByPatientUid, medecinUid, savePatient,getPatientByUid, getDocuments } from 'src/firebaseConfig';
import {  patientUidA } from '../patient/patient.page';
import { Router } from '@angular/router';
import { finalize } from 'rxjs';
import { AngularFireStorage } from '@angular/fire/compat/storage';



interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string; // Ajout de la propriété medecinNom comme optionnelle
  medecinPrenom?: string; 
  medecinuid:string;// Ajout de la propriété medecinPrenom comme optionnelle
  key:string ;
  excuse?:string;
  statut?:string;
}
interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}
interface PatientData{
  uid:string;
  nom: string;
  prenom: string;
  age?: number;
  sexe: string;
  groupeSanguin?: string;
  situationFamiliale?: string;
  profession?: string;
  email: string;
  telephone: string;
  telephone2?: string;
  numeroAssurance?: string;
  adresse?: string;
  note?: string;
  antecedentsMedicaux?: string;
  antecedentsChirurgicaux?: string;
  antecedentsFamiliaux?: string;
  autresAntecedents?: string;
  medicaments?: string;
  useruid:string;
}
interface Documentdata{
  url :string;
  description:string;
}
@Component({
  selector: 'app-patientpage',
  templateUrl: './patientpage.page.html',
  styleUrls: ['./patientpage.page.scss'],
})


export class PatientpagePage implements OnInit {
  rendezVousData!: RendezVousData;
  Vfiche: boolean = true;
  Vconsultation: boolean = false;
  Vdocument: boolean = false;

    uid:string='';
    nom:string='';
    prenom:string='';
    age:number=0;
    adresse:string='';
    telephone:string='';
    sexe:string='';
    groupeSanguin:string='';
    situationFamiliale:string='';
    profession:string='';
    email:string='';
    telephone2:string='';
    numeroAssurance:string='';
    note:string='';
    antecedentsMedicaux:string='';
    antecedentsChirurgicaux:string='';
    antecedentsFamiliaux:string='';
    autresAntecedents:string='';
    medicaments:string='';
    description:string='';


  rdv: RendezVousData[] | null | undefined;
  patient: PatientData | null | undefined;
  consultations!: consultationData[];
  selectedFile: File | null = null;
  selectedFileUrl: string | null = null;
  documents:Documentdata[]=[];

  constructor(private storage : AngularFireStorage ,private firestore: AngularFirestore,private toastController :ToastController, private modalController: ModalController, private cdr: ChangeDetectorRef, private router: Router) {
    this.rendezVousData = {} as RendezVousData;
  }

  async ngOnInit() {
    const doc=await getDocuments(this.firestore,medecinUid.uid,patientUidA)
    console.log(doc)
    console.log(patientUidA)
    this.getdocument()
    try {
      this.patient = await getPatientByUid(this.firestore, medecinUid.uid, patientUidA);
      console.log('patient', this.patient);
      if(this.patient){
     const uuid=this.patient.useruid;
    }
      await this.loadConsultations();
      console.log('rdv', this.rdv);
    } catch (error) {
      console.error('Erreur lors de l\'initialisation de la page :', error);
    }
  }
  async back(){

    this.router.navigate(['/patient']);
    console.log('going to patient')
  }
  async loadConsultations() {
    try {
      const consultations = await getConsultations(this.firestore, medecinUid.uid, patientUidA);
      console.log('consultations', consultations);

      if (consultations && consultations.length > 0) {
        this.consultations = consultations.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
      } else {
        console.log('Aucune consultation trouvée pour ce patient.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement des consultations du patient :', error);
    }
  }


  async saveFichePatient() {
    try {
        console.log('Données de la fiche patient à sauvegarder :', this.adresse,this.age,this.antecedentsChirurgicaux,this.antecedentsFamiliaux,this.antecedentsMedicaux,this.autresAntecedents,this.email,this.medicaments,this.groupeSanguin,this.nom,this.note,this.numeroAssurance,this.prenom,this.profession,this.sexe,this.situationFamiliale,this.telephone,this.telephone2,this.uid);
        await savePatient(this.firestore, this.adresse,this.age,this.antecedentsChirurgicaux,this.antecedentsFamiliaux,this.antecedentsMedicaux,this.autresAntecedents,this.email,this.medicaments,this.groupeSanguin,this.nom,this.note,this.numeroAssurance,this.prenom,this.profession,this.sexe,this.situationFamiliale,this.telephone,this.telephone2,patientUidA, medecinUid.uid);

    } catch (error) {
      console.error('Erreur lors de l\'ajout du patient chez le médecin :', error);
    }
  }

  fiche() {
    this.Vconsultation = false;
    this.Vdocument = false;
    this.Vfiche = true;
  }

  consultation() {
    this.Vconsultation = true;
    this.Vdocument = false;
    this.Vfiche = false;
  }

  document() {
    this.Vconsultation = false;
    this.Vdocument = true;
    this.Vfiche = false;
  }




  async onFileSelected(event: any) {
    const file: File | null = event.target.files[0];
    this.selectedFile = file;

    if (file instanceof File) {
      try {
        // Générer un ID unique pour le fichier
        const fileId = Math.random().toString(36).substring(2);
        const filePath = `documents/${fileId}_${file.name}`;

        // Uploader le fichier dans Firebase Storage
        const fileRef = this.storage.ref(filePath);
        const uploadTask = this.storage.upload(filePath, file);

        // Récupérer l'URL permanente du fichier une fois qu'il est téléchargé
        uploadTask.snapshotChanges().pipe(
          finalize(async () => {
            const fileUrl = await fileRef.getDownloadURL().toPromise();

            // Maintenant, vous pouvez utiliser fileUrl comme lien permanent pour le fichier
            console.log('Lien permanent du fichier :', fileUrl);

            // Ajouter le document avec le lien permanent dans Firestore
            const docRef = this.firestore.collection('medecin').doc(medecinUid.uid).collection('documents').doc();
            await docRef.set({
              medecinUid: medecinUid.uid,
              patientUid: patientUidA,
              url: fileUrl,
              description: this.description
            });
            this.getdocument();
            console.log('Document ajouté avec succès.');
          })
        ).subscribe();

      } catch (error) {
        console.error('Erreur lors de l\'ajout du document :', error);
      }
    }
  }
  async envoyer() {
    if (!this.selectedFile) {
      console.error('Aucun fichier sélectionné.');
      return;
    }

    // Vérifier si la description est vide
    if (!this.description) {

      const toast = await this.toastController.create({
        message: 'La description est obligatoire.',
        duration: 1500, // Durée du toast en millisecondes
        position: 'top', // Position du toast (peut être 'top', 'bottom', 'middle')
        color: 'danger', // Couleur du toast
      });
      await toast.present();
      return;
    }

    // Générer un ID unique pour le fichier
    const fileId = this.firestore.createId();

    // Chemin de stockage dans Firebase Storage
    const filePath = `documents/${fileId}`;

    try {
      // Télécharger le fichier vers Firebase Storage
      const fileRef = this.storage.ref(filePath);
      const uploadTask = this.storage.upload(filePath, this.selectedFile);

      // Attendre la fin du téléchargement
      await uploadTask.then();

      // Récupérer l'URL du fichier téléchargé
      const fileUrl = await fileRef.getDownloadURL().toPromise();

      // Enregistrer les métadonnées dans Firestore
      await this.firestore.collection('documents').doc(fileId).set({
        useruid:this.patient?.useruid,
        url: fileUrl,
        description: this.description,
        // Ajoutez d'autres champs si nécessaire
      });
      const documents = await getDocuments(this.firestore, medecinUid.uid, patientUidA);
      if (documents && documents.length > 0) {
        this.documents = documents;}
      console.log('Fichier téléchargé et enregistré avec succès.');

        const docu = {
          url: fileUrl,
          description: this.description,
          med:medecinUid.uid,
        }
        const medref =await this.firestore.collection('inscrits').doc(this.patient?.useruid).collection('document').add(docu);





    } catch (error) {
      console.error('Erreur lors de l\'envoi du fichier :', error);
    }
    }


  async getdocument(){
    const documents=await  getDocuments(this.firestore,medecinUid.uid,patientUidA);
    if (documents && documents.length > 0) {
      this.documents=documents;

  }
  }
  closeModal() {
    this.modalController.dismiss();
  }












}
