import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-imc',
  templateUrl: './imc.page.html',
  styleUrls: ['./imc.page.scss'],
})
export class ImcPage implements OnInit {
  poids: number = 0;
  taille: number = 0;
  imc: number = 0;

  constructor() { }

  ngOnInit() { }

  calculer() {
    this.imc = this.poids / Math.pow((this.taille / 100), 2);
  }
}
