import { Component,ChangeDetectorRef, Input, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ModalController } from '@ionic/angular';
import { getConsultations, getFichePatient, getRdvByPatientUid, medecinUid, savePatient,getPatientByUid } from 'src/firebaseConfig';
import { patientA, patientUidA } from '../patient/patient.page';
import { meduid } from '../login-medecin/login-medecin.page';


interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string; // Ajout de la propriété medecinNom comme optionnelle
  medecinPrenom?: string; 
  medecinuid:string;// Ajout de la propriété medecinPrenom comme optionnelle
  key:string ;
  excuse?:string;
  statut?:string;
}
interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}
interface PatientData{
  uid:string;
  nom: string;
  prenom: string;
  age?: number;
  sexe: string;
  groupeSanguin?: string;
  situationFamiliale?: string;
  profession?: string;
  email: string;
  telephone: string;
  telephone2?: string;
  numeroAssurance?: string;
  adresse?: string;
  note?: string;
  antecedentsMedicaux?: string;
  antecedentsChirurgicaux?: string;
  antecedentsFamiliaux?: string;
  autresAntecedents?: string;
  medicaments?: string;
}
@Component({
  selector: 'app-patientpage',
  templateUrl: './patientpage.page.html',
  styleUrls: ['./patientpage.page.scss'],
})


export class PatientpagePage implements OnInit {
  rendezVousData!: RendezVousData;
  Vfiche: boolean = true;
  Vconsultation: boolean = false;
  Vdocument: boolean = false;

    uid:string='';
    nom:string='';
    prenom:string='';
    age:number=0;
    adresse:string='';
    telephone:string='';
    sexe:string='';
    groupeSanguin:string='';
    situationFamiliale:string='';
    profession:string='';
    email:string='';
    telephone2:string='';
    numeroAssurance:string='';
    note:string='';
    antecedentsMedicaux:string='';
    antecedentsChirurgicaux:string='';
    antecedentsFamiliaux:string='';
    autresAntecedents:string='';
    medicaments:string='';

  rdv: RendezVousData[] | null | undefined;
  patient: PatientData | null | undefined;
  consultations!: consultationData[];
  selectedFile: File | null = null;
  selectedFileUrl: string | null = null;

  constructor(private firestore: AngularFirestore, private modalController: ModalController, private cdr: ChangeDetectorRef) {
    this.rendezVousData = {} as RendezVousData;
  }

  async ngOnInit() {
    try {
      this.patient = await getPatientByUid(this.firestore, medecinUid.uid, patientUidA);
      console.log('patient', this.patient);

      await this.loadConsultations();
      console.log('rdv', this.rdv);
    } catch (error) {
      console.error('Erreur lors de l\'initialisation de la page :', error);
    }
  }

  async loadConsultations() {
    try {
      const consultations = await getConsultations(this.firestore, medecinUid.uid, patientUidA);
      console.log('consultations', consultations);

      if (consultations && consultations.length > 0) {
        // Trier les consultations de l'ancien au plus récent
        this.consultations = consultations.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
      } else {
        console.log('Aucune consultation trouvée pour ce patient.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement des consultations du patient :', error);
    }
  }


  async saveFichePatient() {
    try {
        console.log('Données de la fiche patient à sauvegarder :', this.adresse,this.age,this.antecedentsChirurgicaux,this.antecedentsFamiliaux,this.antecedentsMedicaux,this.autresAntecedents,this.email,this.medicaments,this.groupeSanguin,this.nom,this.note,this.numeroAssurance,this.prenom,this.profession,this.sexe,this.situationFamiliale,this.telephone,this.telephone2,this.uid);
        await savePatient(this.firestore, this.adresse,this.age,this.antecedentsChirurgicaux,this.antecedentsFamiliaux,this.antecedentsMedicaux,this.autresAntecedents,this.email,this.medicaments,this.groupeSanguin,this.nom,this.note,this.numeroAssurance,this.prenom,this.profession,this.sexe,this.situationFamiliale,this.telephone,this.telephone2,patientUidA, medecinUid.uid);

    } catch (error) {
      console.error('Erreur lors de l\'ajout du patient chez le médecin :', error);
    }
  }

  fiche() {
    this.Vconsultation = false;
    this.Vdocument = false;
    this.Vfiche = true;
  }

  consultation() {
    this.Vconsultation = true;
    this.Vdocument = false;
    this.Vfiche = false;
  }

  document() {
    this.Vconsultation = false;
    this.Vdocument = true;
    this.Vfiche = false;
  }

  onFileSelected(event: any) {
    const file: File | null = event.target.files[0];
    if (file) {
      this.selectedFile = file;
      this.selectedFileUrl = URL.createObjectURL(this.selectedFile);
      console.log('URL de l\'image sélectionnée : ', this.selectedFileUrl);
    }
  }

  closeModal() {
    this.modalController.dismiss();
  }
}
