import { Component, Input, NgModule } from '@angular/core';
import { IonicModule, ModalController } from '@ionic/angular';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import {  selectedHour } from '../work-hours-modal/work-hours-modal.component';
import { selectedNom, selectedPrenom } from '../personne-modal/personne-modal.component';
import { medecinUid } from 'src/app/recherche-medecin/recherche-medecin.page';
import { inscritUid } from 'src/app/login/login.page';
import {AddRendezVous, AddRendezVousForUser} from 'src/firebaseConfig';




interface MedecinData {
  nom: string;
  prenom: string;
  uid: string;
}

interface RendezVousData {
  date: string;
  heure: string;
  nom:string;
  prenom: string;
  useruid: string;

}

@Component({
  selector: 'app-rendezvousmodal',
  templateUrl: './rendezvousmodal.component.html',
  styleUrls: ['./rendezvousmodal.component.scss'],
})
export class RendezvousmodalComponent {
  @Input() medecin: MedecinData = { nom: '', prenom: '', uid: '' }; // Initialisez la propriété medecin

  selectedTime = selectedHour; 
  patientName= selectedNom; 
  uid: any;



  constructor(private modalController: ModalController, private firestore: AngularFirestore) {}

  closeModal() {
    this.modalController.dismiss();
  }

  ngOnInit() {
    console.log("prenom = ",selectedNom);
    console.log("time = ",selectedHour);
    console.log("medecinuid =",medecinUid);
    console.log("useruid =",inscritUid);
  }

  saveRendezVous() {
    // Logique pour enregistrer le rendez-vous dans la sous-collection rdv du médecin
    const rendezVousData: RendezVousData = {
      date: new Date().toISOString().split('T')[0], // Date actuelle
      heure: selectedHour,
      nom :selectedNom,
      prenom: selectedPrenom,
      useruid : inscritUid, // Utilisation de uidPatient au lieu de idPatient

    };

    // Enregistrez les données du rendez-vous en utilisant la fonction AddRendezVous
    AddRendezVous(this.firestore, medecinUid, rendezVousData)
      .then(() => {
        console.log('Rendez-vous enregistré avec succès.');
        this.closeModal(); // Fermez le modal après avoir enregistré le rendez-vous
      })
      .catch((error) => {
        console.error('Erreur lors de l\'enregistrement du rendez-vous :', error);
      });
    AddRendezVousForUser(this.firestore, inscritUid, rendezVousData)
    .then(() => {
      console.log('Rendez-vous enregistré avec succès chez le patient.');
      this.closeModal(); // Fermez le modal après avoir enregistré le rendez-vous
    })
    .catch((error) => {
      console.error('Erreur lors de l\'enregistrement du rendez-vous chez le patient:', error);
    });
  }

}



@NgModule({
  declarations: [RendezvousmodalComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule
  ],
  providers: [AngularFirestore],
})
export class RendezvousmodalModule {}



<ion-header>
  <ion-toolbar>
    <ion-title>Ajouter un rendez-vous</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="closeModal()">
        <ion-icon name="close"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-content>
    <!-- Affichage de l'heure sélectionnée -->
    <p>Heure sélectionnée : {{ selectedTime }}</p>

    <!-- Affichage du nom du patient -->
    <p>Nom du patient : {{ patientName }}</p>

    <!-- Affichage de l'UID du patient -->
   <!-- <p>UID du patient : {{ uidPatient }}</p> -->
  </ion-content>

  <div class="modal-buttons">
    <ion-button expand="block" color="primary" (click)="saveRendezVous()">Enregistrer</ion-button>
  </div>
</ion-content>
