import { Component, OnInit } from '@angular/core';
import {  getRdvByMedUid, medecinUid } from 'src/firebaseConfig';
import { meduid } from '../login-medecin/login-medecin.page';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ToastController } from '@ionic/angular';
interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string;
  medecinPrenom?: string;
  medecinuid: string;
  key: string;
  excuse?:string ;
  statut?:string;
  present?:boolean;
}


@Component({
  selector: 'app-salle',
  templateUrl: './salle.page.html',
  styleUrls: ['./salle.page.scss'],
})
export class SallePage implements OnInit {
uid=medecinUid.uid
rdvs:RendezVousData[]=[];
present:boolean=true;
p:boolean=true;
constructor(private firestore: AngularFirestore,private toastController :ToastController) { }

  ngOnInit() {
    this.loadEvents();
  }
  async loadEvents() {
    const rdvs = await getRdvByMedUid(this.firestore, this.uid);
    if (rdvs && rdvs.length > 0) {
      const today = new Date();
      today.setHours(0, 0, 0, 0); // Set the time to 00:00:00 to compare only the date
      this.rdvs = rdvs
        .filter(rdv => new Date(rdv.date).setHours(0, 0, 0, 0) === today.setHours(0, 0, 0, 0))
        .map(rdv => {
          rdv.present = false;
          return rdv;
        })
        .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
      console.log(rdvs);

    }
    else{
      this.p=false ;
      console.log("vous n'avez pas de rendezvous aujourd'hui");

      const toast = await this.toastController.create({
        message: 'vous n/avez pas de rendez-vous veuillez retournez avec le bouton retour',
        duration: 1500, // Durée du toast en millisecondes
        position: 'top', // Position du toast (peut être 'top', 'bottom', 'middle')
        color: 'danger', // Couleur du toast
      });

    }
  }
    }
