import { Component, OnInit } from '@angular/core';
import { CalendarEvent } from 'angular-calendar';
import { isSameDay, isSameMonth } from 'date-fns';
import { CalendarView } from 'angular-calendar';
import {  deleteRendezVous, getRdvByMedUid } from 'src/firebaseConfig';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { medecinUid  } from 'src/firebaseConfig';

interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string; // Ajout de la propriété medecinNom comme optionnelle
  medecinPrenom?: string; 
  medecinuid:string;// Ajout de la propriété medecinPrenom comme optionnelle
  key:string ;
}


@Component({
  selector: 'app-calender-medecin',
  templateUrl: './calender-medecin.page.html',
  styleUrls: ['./calender-medecin.page.scss'],
})
export class CalenderMedecinPage implements OnInit {

  ngOnInit() {}

  constructor(private firestore: AngularFirestore) {
    this.loadEvents();
    console.log('meduid', medecinUid.uid);

  }

  viewDate: Date = new Date();
  view: CalendarView = CalendarView.Month;
  CalendarView = CalendarView;
  uid: string = medecinUid.uid;
  events: CalendarEvent[] = [{ start: new Date(), title: 'An event' }];
  activeDayIsOpen: boolean = false;
  rdvs: RendezVousData[] = [];
  medecinuid=this.uid;


  async loadEvents() {
    try {
      const rdvs = await getRdvByMedUid(this.firestore, this.uid);
      if (rdvs && rdvs.length > 0) {
        this.rdvs = rdvs;
        this.events = rdvs.map(rdv => ({
          start: this.convertDateTimeToDate(rdv.date, rdv.heure.split(' - ')[0]),
          end: this.convertDateTimeToDate(rdv.date, rdv.heure.split(' - ')[1]),
          title: `Rendez-vous de ${rdv.prenom} ${rdv.nom} à ${rdv.heure} chez ${rdv.medecinPrenom} ${rdv.medecinNom}  `,
        }));
      }
    } catch (error) {
      console.error('Erreur lors du chargement des rendez-vous:', error);
    }
  }

  dayClicked({ date, events }: { date: Date; events: CalendarEvent[] }): void {
    if (isSameMonth(date, this.viewDate)) {
      if ((isSameDay(this.viewDate, date) && this.activeDayIsOpen === true) || events.length === 0) {
        this.activeDayIsOpen = false;
      } else {
        this.activeDayIsOpen = true;
        this.viewDate = date;
      }
    }
  }
  async deleteRendezVouscalendar(rendezVous: RendezVousData) {
    try {
      await deleteRendezVous(this.firestore, this.uid, rendezVous.key);
      console.log('Rendez-vous supprimé avec succès.');
      // Mettez ici le code pour actualiser l'affichage des rendez-vous sur la page
    } catch (error) {
      console.error('Erreur lors de la suppression du rendez-vous:', error);
    }
}



  changeDay(date: Date) {
    this.viewDate = date;
    this.view = CalendarView.Day;
  }

  closeOpenMonthViewDay() {
    this.activeDayIsOpen = false;
  }

  setView(view: CalendarView) {
    this.view = view;
  }

  private convertDateTimeToDate(dateString: string, timeString: string): Date {
    const dateParts = dateString.split('-').map(part => parseInt(part, 10));
    const timeParts = timeString.split('H').map(part => parseInt(part, 10));
    return new Date(dateParts[0], dateParts[1] - 1, dateParts[2], timeParts[0], timeParts[1]);
  }
}
<ion-header [translucent]="true">
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.2.3/dist/css/bootstrap.min.css" rel="stylesheet">
  <link href="https://unpkg.com/@fortawesome/fontawesome-free@6.2.1/css/all.css" rel="stylesheet">
  <link href="https://unpkg.com/angular-calendar@0.31.0/css/angular-calendar.css" rel="stylesheet">
  <link href="https://unpkg.com/flatpickr@4.6.13/dist/flatpickr.css" rel="stylesheet">
  <ion-toolbar>
    <ion-title>Calendrier médical</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Calendrier médical</ion-title>
    </ion-toolbar>
  </ion-header>
  <br><br><br>

  <div class="row text-center">
    <div class="col-md-4">
      <div class="btn-group">
        <div
          class="btn btn-primary"
          mwlCalendarPreviousView
          [view]="view"
          [(viewDate)]="viewDate"
          (viewDateChange)="closeOpenMonthViewDay()"
        >
          Précédent
        </div>
        <div
          class="btn btn-outline-secondary"
          mwlCalendarToday
          [(viewDate)]="viewDate"
        >
          Aujourd'hui
        </div>
        <div
          class="btn btn-primary"
          mwlCalendarNextView
          [view]="view"
          [(viewDate)]="viewDate"
          (viewDateChange)="closeOpenMonthViewDay()"
        >
          Suivant
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <h3>{{ viewDate | calendarDate:(view + 'ViewTitle'):'en' }}</h3>
    </div>
    <div class="col-md-4">
      <div class="btn-group">
        <div
          class="btn btn-primary"
          (click)="setView(CalendarView.Month)"
          [class.active]="view === CalendarView.Month"
        >
          Mois
        </div>
        <div
          class="btn btn-primary"
          (click)="setView(CalendarView.Week)"
          [class.active]="view === CalendarView.Week"
        >
          Semaine
        </div>
        <div
          class="btn btn-primary"
          (click)="setView(CalendarView.Day)"
          [class.active]="view === CalendarView.Day"
        >
          Jour
        </div>
      </div>
    </div>
  </div>
  <br />

  <div [ngSwitch]="view">
    <mwl-calendar-month-view
        [viewDate]="viewDate"
        [events]="events"
        [activeDayIsOpen]="activeDayIsOpen"
        (dayClicked)="dayClicked($event.day)"
      >
    </mwl-calendar-month-view>
    <mwl-calendar-week-view
      *ngSwitchCase="'week'"
      [viewDate]="viewDate"
      [events]="events"
      (dayHeaderClicked)="changeDay($event.day.date)"
    >
    </mwl-calendar-week-view>
    <mwl-calendar-day-view
      *ngSwitchCase="'day'"
      [viewDate]="viewDate"
      [events]="events"
    >
    </mwl-calendar-day-view>
  </div>
  <ion-list>
    <ion-item *ngFor="let rendezVousItem of rdvs">
      <ion-label>{{ rendezVousItem.date }} - {{ rendezVousItem.heure }} - {{ rendezVousItem.nom }} {{ rendezVousItem.prenom }} {{ rendezVousItem.key }}  </ion-label>
      <ion-button (click)="deleteRendezVouscalendar(rendezVousItem)" color="danger">Supprimer</ion-button>
    </ion-item>
  </ion-list>


</ion-content>
<ion-footer collapse="fade">
  <ion-toolbar>
    <ion-tabs>
      <ion-tab-bar slot="bottom">
        <ion-tab-button routerLink="/app-medecin">
          <ion-icon name="home-outline"></ion-icon>
          Accueil
        </ion-tab-button>
        <ion-tab-button routerLink="/calender-medecin" [routerDirection]="'root'">
          <ion-icon name="calendar-outline"></ion-icon>
          Calendrier
        </ion-tab-button>
        <ion-tab-button routerLink="/parametre-med" [routerDirection]="'root'">
          <ion-icon name="settings-outline"></ion-icon>
          Réglages
        </ion-tab-button>
      </ion-tab-bar>
    </ion-tabs>
  </ion-toolbar>
</ion-footer>
