import { Component, OnInit } from '@angular/core';
import { AngularFirestore, DocumentData } from '@angular/fire/compat/firestore';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastController } from '@ionic/angular';
import { getDocumentsByMedecin, medecinUid } from 'src/firebaseConfig';
interface Documentdata{
  url:string;
  description:string;
}
@Component({
  selector: 'app-documentmedecin',
  templateUrl: './documentmedecin.page.html',
  styleUrls: ['./documentmedecin.page.scss'],
})

export class DocumentmedecinPage implements OnInit {
  filteredDocuments:Documentdata[]=[];

  constructor(
    private route: ActivatedRoute,
    private firestore: AngularFirestore,
    private router: Router,
    private toastController: ToastController
  ) {}
documents:Documentdata[]=[];
  async ngOnInit() {
    const documents = await getDocumentsByMedecin(this.firestore, medecinUid.uid);
    this.documents=documents;
    console.log('Documents du médecin:', documents);
  }

  async search(event: any) {
    const searchTerm = event.target.value.toLowerCase(); // Convertir en minuscules pour la comparaison
    if (!searchTerm) {
      // Si la barre de recherche est vide, réinitialiser la liste des patients
      const documents = await getDocumentsByMedecin(this.firestore, medecinUid.uid);
      this.documents = documents;
      return;
    }
    console.log('Le mot saisie est  :', searchTerm);
    const documents = await getDocumentsByMedecin(this.firestore, medecinUid.uid);

    // Filtrer les documents en fonction de la description
    this.filteredDocuments = documents.filter((document) =>
      document.description.toLowerCase().startsWith(searchTerm)
    );
    console.log('Les documents saisis sont :', this.filteredDocuments);

    this.documents = this.filteredDocuments;
  }


}
