import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getConsultations, getConsultationsmed, getPatientsByMedUid } from 'src/firebaseConfig'; 
import { medecinUid } from 'src/firebaseConfig';
import { patientUidA } from '../patient/patient.page';

interface consultationData {
  date: string;
  details?: string;

}
interface PatientData{
  uid:string;
  nom: string;
  prenom: string;
  age: number;
  sexe: string;
  groupeSanguin: string;
  situationFamiliale: string;
  profession: string;
  email: string;
  telephone: string;
  telephone2: string;
  numeroAssurance: string;
  adresse: string;
  note: string;
  antecedentsMedicaux: string;
  antecedentsChirurgicaux: string;
  antecedentsFamiliaux: string;
  autresAntecedents: string;
  medicaments: string;
  useruid:string;
}

@Component({
  selector: 'app-consultation-med',
  templateUrl: './consultation-med.page.html',
  styleUrls: ['./consultation-med.page.scss'],
})


export class ConsultationMedPage implements OnInit {
  consultations: consultationData[] = [];
  uid: string = medecinUid.uid;
  patients:PatientData[]=[];

  constructor(private firestore: AngularFirestore) { }

  async ngOnInit() {
 this.getPatients()
  }
  async loadConsultations(patientuid:string) {
    try {
      const consultations = await getConsultations(this.firestore, medecinUid.uid, patientuid);
      console.log('consultations', consultations);

      if (consultations && consultations.length > 0) {
        this.consultations = consultations.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
      } else {
        console.log('Aucune consultation trouvée pour ce patient.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement des consultations du patient :', error);
    }
  }
  async getPatients() {
    try {
      const patients = await getPatientsByMedUid(this.firestore, this.uid);
      if (this.patients.length > 0) {
        this.patients=patients;
        for (const patient of this.patients) {
          await this.loadConsultations(patient.uid);
        }
      } 
      console.log("Patients trouvés :", this.patients);
    } catch (error) {
      console.error('Erreur lors de la récupération des patients :', error);
    }
  }
}
