import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ActivatedRoute, NavigationExtras, Router } from '@angular/router';
import { ToastController } from '@ionic/angular';
import { getMedecinforPatient } from 'src/firebaseConfig';
import { inscritUid } from '../login/login.page';

interface MedecinData {
  uid:string,
  nom:string,
  prenom:string,
  sexe:string,
  email: string,
  password: string,
  date: string,
  delegation: string,
  etat: string,
  specialite: string,
  telephone: string,
}
@Component({
  selector: 'app-medecin',
  templateUrl: './medecin.page.html',
  styleUrls: ['./medecin.page.scss'],
})
export class MedecinPage implements OnInit {
  medecins1: MedecinData[] = [];

  constructor(
    private route: ActivatedRoute,
    private firestore: AngularFirestore,
    private router: Router,
    private toastController: ToastController
  )  { }


  ngOnInit() {
    this.getMedecinsForPatient();
    console.log(this.medecins1)
  }
  async getMedecinsForPatient() {
    try {
      const medecins1 = await getMedecinforPatient(this.firestore, inscritUid);
      this.medecins1=medecins1;
      console.log('Médecins pour le patient:', medecins1);
    } catch (error) {
      console.error('Erreur lors de la récupération des médecins pour le patient:', error);
    }
  }
  async showConsultations(uid :string) {
    meduidz=uid;
    this.router.navigate(['/consultationliste']);

  }
}
export let meduidz:string;
