import { Component, OnInit } from '@angular/core';
import { ModalController } from '@ionic/angular';

@Component({
  selector: 'app-work-hours-modal',
  templateUrl: './work-hours-modal.component.html',
  styleUrls: ['./work-hours-modal.component.scss'],
})
export class WorkHoursModalComponent implements OnInit {

  workHours: { start: string, end: string }[] = [
    // Your work hours data
  ];

  constructor(private modalController: ModalController) { }

  ngOnInit() {
    // Fetch reserved hours or any other initialization logic if needed
  }

  close() {
    this.modalController.dismiss();
  }

  selectHour(hour: string) {
    // Logic to handle when patient selects an hour in the modal
    // For example, you can pass the selected hour back to the parent component using modal dismiss with data
    this.modalController.dismiss({ selectedHour: hour });
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title>Heures de travail</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="close()">Fermer</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <div *ngFor="let hour of workHours">
    <ion-button [disabled]="isHourReserved(hour.start)" (click)="selectHour(hour.start)">
      {{ hour.start }}
    </ion-button>
  </div>
</ion-content>
