import { CommonModule } from '@angular/common';
import { Component, Input, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { IonicModule, ModalController } from '@ionic/angular';
@Component({
  selector: 'app-supprimer-alert',
  templateUrl: './supprimer-alert.component.html',
  styleUrls: ['./supprimer-alert.component.scss'],
})
export class SupprimerAlertComponent {
  @Input() secretaireID!: string;


  constructor(private modalController: ModalController) {}

  confirmerSuppression() {
    this.modalController.dismiss({ confirmed: true });
  }

  fermerModal() {
    this.modalController.dismiss();
  }
}
@NgModule({
  declarations: [SupprimerAlertComponent],
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    IonicModule,
  ],
  exports: [SupprimerAlertComponent],
  providers: [AngularFirestore],
})
export class SupprimerAlertModule {}
