import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getMedecinByUid, medecinUid } from 'src/firebaseConfig';// Importez votre fonction getMedecinByUid



interface MedecinData {
  nom:string,
  prenom:string,
  sexe:string,
  email: string,
  password: string,
  date: string,
  delegation: string,
  etat: string,
  specialite: string,
  telephone: string,
}

@Component({
  selector: 'app-parametre-med-profil',
  templateUrl: './parametre-med-profil.page.html',
  styleUrls: ['./parametre-med-profil.page.scss'],
})


export class ParametreMedProfilPage implements OnInit {

  medecin: MedecinData[] | null=[];
  formulaireActif: boolean = false; // Indicateur pour activer/désactiver le formulaire
  uid=medecinUid.uid;
  constructor(private router: Router, private firestore: AngularFirestore) {}
  nom:string="";
  prenom:string="";
  sexe:string="";
  email: string="";
  password: string="";
  date: string="";
  delegation: string="";
  etat: string="";
  specialite: string="";
  telephone: string="";


  ngOnInit() {

    if (this.uid) {
      console.log('Médecin UID récupéré non :', this.uid);
      this.fetchMedecin(this.uid);
      if(this.medecin){
      console.log(this.medecin[0].nom);
      this.nom=this.medecin[0].nom;
      this.prenom=this.medecin[0].prenom;
      this.sexe=this.medecin[0].sexe;
      this.email=this.medecin[0].email;
      this.password=this.medecin[0].password;
      this.date=this.medecin[0].date;
      this.delegation=this.medecin[0].delegation;
      this.etat= this.medecin[0].etat;
      this.specialite=this.medecin[0].specialite;
      this.telephone= this.medecin[0].telephone;
      }
    } else {
      console.error('Erreur: medecinUid est undefined.');
    }
  }


  async fetchMedecin(medecinUidValue: string) {
    try {

      this.medecin = await getMedecinByUid(this.firestore, medecinUidValue);
      console.log('Médecin récupéré :', this.medecin);
    } catch (error) {
      console.error('Erreur lors de la récupération du médecin :', error);
    }
  }


  activerFormulaire() {
    this.formulaireActif = true; // Active le formulaire pour les modifications
  }


  async enregistrerDonnees(formulaire: any) {
    try {
      // Créez un objet pour les données du médecin à mettre à jour
      const medecinA = {
        nom: this.nom,
        prenom: this.prenom,
        sexe: this.sexe,
        email: this.email,
        password: this.password,
        date: this.date,
        delegation: this.delegation,
        etat: this.etat,
        specialite: this.specialite,
        telephone: this.telephone
      };

      // Mettez à jour les données du médecin dans la base de données Firebase
      await this.firestore.collection('medecin').doc(this.uid).update(medecinA);

      console.log('Données du médecin mises à jour avec succès dans Firebase.');

      // Désactivez le formulaire après la mise à jour
      this.formulaireActif = false;
    } catch (error) {
      console.error('Erreur lors de la mise à jour des données du médecin dans Firebase :', error);
    }
  }
}  





<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-button routerLink="/parametre-med" fill="clear" slot="start"> Retour</ion-button>
    <ion-title>Paramètre du profil du médecin</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Paramètre du profil du médecin</ion-title>
    </ion-toolbar>
  </ion-header>


      <h3>Paramètres du médecin</h3> 

      <form #medecinForm="ngForm" >
        <ion-item>
          <ion-label position="floating">Nom</ion-label>
          <ion-input type="text" [(ngModel)]="nom" name="nom" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Prénom</ion-label>
          <ion-input type="text" [(ngModel)]="prenom" name="prenom" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Email</ion-label>
          <ion-input type="email" [(ngModel)]="medecin[0].email" name="email" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Mot de passe</ion-label>
          <ion-input type="password" [(ngModel)]="medecin[0].password" name="password" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Date de naissance</ion-label>
          <ion-input type="date" [(ngModel)]="medecin[0].date" name="date" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Sexe</ion-label>
          <ion-select [(ngModel)]="medecin[0].sexe" name="sexe" [disabled]="!formulaireActif">
            <ion-select-option value="homme">Homme</ion-select-option>
            <ion-select-option value="femme">Femme</ion-select-option>
          </ion-select>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Rôle</ion-label>
          <ion-input type="text" [(ngModel)]="medecin[0].role" name="role" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Spécialité</ion-label>
          <ion-input type="text" [(ngModel)]="medecin[0].specialite" name="specialite" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Téléphone</ion-label>
          <ion-input type="tel" [(ngModel)]="medecin[0].telephone" name="telephone" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Delegation</ion-label>
          <ion-input type="text" [(ngModel)]="medecin[0].delegation" name="delegation" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-item>
          <ion-label position="floating">Etat</ion-label>
          <ion-input type="text" [(ngModel)]="medecin[0].etat" name="etat" [disabled]="!formulaireActif"></ion-input>
        </ion-item>
        <ion-row>
          <ion-col>
            <ion-button expand="block" color="primary" (click)="activerFormulaire()">Modifier</ion-button>
          </ion-col>
          <ion-col>
            <ion-button expand="block" color="success" type="submit" [disabled]="!formulaireActif" (click)="enregistrerDonnees(medecinForm)">Enregistrer</ion-button>
          </ion-col>
        </ion-row>
      </form>


</ion-content>
