import { Component,ChangeDetectorRef, Input, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ModalController } from '@ionic/angular';
import { getConsultations, getRdvByPatientUid, medecinUid, savePatient } from 'src/firebaseConfig';
import { patientA, patientUidA } from '../patient/patient.page';
import { meduid } from '../login-medecin/login-medecin.page';

interface PatientData {
  nom: string;
  prenom: string;
  age?: number;
  adresse?: string;
  telephones?: string;
  uid:string ;
}

interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string; // Ajout de la propriété medecinNom comme optionnelle
  medecinPrenom?: string; 
  medecinuid:string;// Ajout de la propriété medecinPrenom comme optionnelle
  key:string ;
  excuse?:string;
  statut?:string;
}
interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}
@Component({
  selector: 'app-patientpage',
  templateUrl: './patientpage.page.html',
  styleUrls: ['./patientpage.page.scss'],
})


export class PatientpagePage implements OnInit {
   rendezVousData!: RendezVousData; 

  Vfiche:boolean=true;
  Vconsultation:boolean=false;
  Vdocument:boolean=false;

  fichePatient = {
    nom: '',
    prenom: '',
    age: null,
    adresse: '',
    telephone: ''
    // Ajoutez d'autres champs selon vos besoins
  };
  rdv: RendezVousData[] | null | undefined;
  patient:PatientData =patientA ;
  consultations!: consultationData[];


  constructor(private firestore: AngularFirestore,private modalController: ModalController,private cdr: ChangeDetectorRef) {
    this.rendezVousData = {} as RendezVousData;

  }

   ngOnInit() {


    console.log('patient',patientA)
    this.loadConsultations();

    console.log('rdv',this.rdv)

  }

  async loadConsultations() {
    try {
      // Utilisez la fonction getConsultation pour obtenir les consultations
      const consultations = await getConsultations(this.firestore,medecinUid.uid, patientA.uid);
      console.log(consultations)

      if (consultations && consultations.length > 0) {
        // Assurez-vous que les consultations sont définies et non vides
        this.consultations = consultations;
      } else {
        console.log('Aucune consultation trouvée pour ce patient.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement des consultations du patient :', error);
    }
  }
  closeModal() {
    this.modalController.dismiss(); // Fermer le modal
  }

  async saveFichePatient() {
    try {
     this.fichePatient.nom=this.patient.nom ;
     this.fichePatient.prenom=this.patient.prenom;
     console.log('prenom ',this.fichePatient.prenom,'nom ',this.fichePatient.nom)

      console.log('Données de la fiche patient à sauvegarder :', this.fichePatient);

      // Appel de la fonction savePatient pour sauvegarder le patient chez le médecin
      await savePatient(this.firestore, this.fichePatient, meduid);


    } catch (error) {
      console.error('Erreur lors de l\'ajout du patient chez le médecin :', error);
    }
  }
  fiche(){
 this.Vconsultation=false;
 this.Vdocument=true;
 this.Vfiche=false;
  }

  consultation(){
    this.Vconsultation=true;
    this.Vdocument=false;
    this.Vfiche=false;
     }

     document(){
      this.Vconsultation=false;
      this.Vdocument=true;
      this.Vfiche=false;
       }


}
