import { CommonModule } from '@angular/common';
import { Component, Input, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { ModalController, IonicModule } from '@ionic/angular';



interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}
interface PatientData{
  uid:string;
  nom: string;
  prenom: string;
  age: number;
  sexe: string;
  groupeSanguin: string;
  situationFamiliale: string;
  profession: string;
  email: string;
  telephone: string;
  telephone2: string;
  numeroAssurance: string;
  adresse: string;
  note: string;
  antecedentsMedicaux: string;
  antecedentsChirurgicaux: string;
  antecedentsFamiliaux: string;
  autresAntecedents: string;
  medicaments: string;
  useruid:string;
}

@Component({
  selector: 'app-modif',
  templateUrl: './modif.component.html',
  styleUrls: ['./modif.component.scss'],
})
export class ModifComponent  implements OnInit {
  @Input() patientData: any; // Input pour recevoir les données du patient

  @Input() medecinUid: string | undefined // Passer l'UID du médecin ici


  newConsultation: { date: Date, details: string } = { date: new Date(), details: '' };
  consultations: { date: Date, details: string }[] = [];

  constructor(private firestore: AngularFirestore, private modalController: ModalController) {}

  ngOnInit() {
    // Chargez les consultations existantes du patient ici
   // this.loadConsultations();
  }



  closeModal() {
    this.modalController.dismiss(); // Fermer le modal
  }

}
@NgModule({
  declarations: [ModifComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule
  ],
  providers: [AngularFirestore],
})
export class ModifModule {}
