// login.page.ts

import { Component, OnInit } from '@angular/core';
import { NavController, ToastController } from '@ionic/angular';
import { loginUser } from 'src/firebaseConfig'; // Assurez-vous que le chemin est correct



@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})



export class LoginPage implements OnInit {

  email: string = '';
  password: string = '';




  constructor(private navCtrl: NavController, private toastController: ToastController) { }


  async loginUser() {
    try {
      const userCredential = await loginUser(this.email, this.password);
      const user = userCredential.user;
      if (user) {
        const uid = user.uid;
        userUid = uid;
        console.log('UID de l\'utilisateur connecté :', uid);
        this.navCtrl.navigateForward('tabs/tab1');
      } else {
        this.presentToast('Identifiants invalides');// Gérez les erreurs d'authentification ici (par exemple, affichez un message à l'utilisateur)
        console.log('le toast d erreur s affiche ');
      }
    } catch (error) {
      console.log('Error during login:', error);

      // Affichez un message à l'utilisateur pour l'informer de l'erreur
    }
  }
  async presentToast(message: string) {
    const toast = await this.toastController.create({
      message: message,
      duration: 3000,
      position: 'bottom',
    });
    toast.present();
  }
  back() {
    this.navCtrl.navigateForward('/home');
  }

  ngOnInit() {
  }
}
export let userUid: string ;


  <ion-content>
    <ion-header collapse="condense">
      <ion-button slot="start" (click)="back()" fill="clear">
        ← Retour
      </ion-button>
    </ion-header>



          <div class="ion-text-center">
            <br><br><br>
            <h4>Connexion</h4>
          </div>
          <br><br><br><br><br><br>
          <div class="form">
            <ion-item>
              <ion-input placeholder="Enter email" type="email" [(ngModel)]="email">

                <ion-icon name="mail-outline">
                </ion-icon>
              </ion-input>
            </ion-item>
            <br>
            <form>
            <ion-item>
              <ion-input placeholder="Enter password" type="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}">

                <ion-icon name="lock-closed-outline"></ion-icon>
              </ion-input>
            </ion-item>
          </form>
            <br>
          </div>
          <div>

            <ion-button (click)="loginUser()" size="large" expand="block" shape="round">Login</ion-button>
            <ion-button routerLink="/forgotpass" fill="clear">
              Forgot Password
            </ion-button>

          </div>

        <ion-button routerLink="/signup" fill="clear">
          Vous n'avez pas de compte ? Créez en un tout de suite .
        </ion-button>

  </ion-content>
