import { CommonModule } from '@angular/common';
import { Component, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { ModalController, IonicModule } from '@ionic/angular';
import { getConsultations } from 'src/firebaseConfig';
import { ConsultationslisteModalComponent } from '../patients/consultationsliste-modal/consultationsliste-modal.component';
import { patientUidA } from '../patient/patient.page';
import { meduid } from '../login-medecin/login-medecin.page';
import { medecinUid } from 'src/firebaseConfig';
import { Router } from '@angular/router';


interface PatientData {
  id: string;
  nom: string;
  prenom: string;
  age?: number;
  adresse?: string;
  telephones?: string;
  uid?: string;
  medecinUid?: string; // Ajout de medecinUid dans PatientData
}
interface consultationData {
  date: string;
  details?: string;
}
@Component({
  selector: 'app-consultationliste',
  templateUrl: './consultationliste.page.html',
  styleUrls: ['./consultationliste.page.scss'],
})
export class ConsultationlistePage implements OnInit {



  consultations: consultationData[] = [];
  constructor(private firestore: AngularFirestore, private modalController: ModalController , private router :Router) {}

  ngOnInit() {

    this.loadConsultations();
    console.log('patientuid',patientUidA);
    console.log('meduid',medecinUid.uid);
  }

  async loadConsultations() {
    try {
      // Utilisez la fonction getConsultation pour obtenir les consultations
      const consultations = await getConsultations(this.firestore,medecinUid.uid, patientUidA);

      if (consultations && consultations.length > 0) {
        // Assurez-vous que les consultations sont définies et non vides
        this.consultations = consultations;
      } else {
        console.log('Aucune consultation trouvée pour ce patient.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement des consultations du patient :', error);
    }
  }
  async showConsultationdetails(date :string) {
     consultationdate=date 
    this.router.navigate(['/consultation']);

  }





}
export let consultationdate: string ;
