import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getDocumentsforpatient, firestore } from 'src/firebaseConfig';
import { inscritUid } from '../login/login.page';
import { ActivatedRoute, Router } from '@angular/router';
import { LoadingController, ToastController } from '@ionic/angular';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { finalize } from 'rxjs';
import { patientUidA } from '../patient/patient.page';

interface Documentdata {
  url: string;
  description: string;
  useruid: string;
}

@Component({
  selector: 'app-document',
  templateUrl: './document.page.html',
  styleUrls: ['./document.page.scss'],
})
export class DocumentPage implements OnInit {
  documents: Documentdata[] = [];
  searchDescription: string = '';
  documentDescription: string = '';
  selectedFile: File | null = null;
  selectedFileUrl: string | null = null;
  document: Documentdata = { url: '', description: '', useruid: inscritUid };

  constructor(
    private route: ActivatedRoute,
    private firestore: AngularFirestore,
    private router: Router,
    private toastController: ToastController,
    private storage: AngularFireStorage,
    private loadingController :LoadingController
  ) {}

  ngOnInit() {
    this.getdocument();
  }

  async getdocument() {
    const documents = await getDocumentsforpatient(this.firestore, inscritUid);
    if (documents) {
      this.documents = documents;
    }
  }

  async searchDocumentsByDescription(description: string) {
    try {
      if (!description) {
        console.log('Veuillez entrer une description.');
        return;
      }

      // Filtrer les documents pour la description spécifiée
      const filteredDocuments = this.documents.filter(document => document.description === description);

      if (filteredDocuments.length > 0) {
        console.log('Documents trouvés avec la description', description, ':', filteredDocuments);
        this.documents = filteredDocuments;
      } else {
        console.log('Aucun document trouvé avec la description', description);
      }
    } catch (error) {
      console.error('Erreur lors de la recherche des documents pour la description :', error);
    }
  }

  async search(event: CustomEvent) {
    const searchTerm = (event.target as HTMLInputElement).value.toLowerCase().trim();
    if (!searchTerm) {
      // Si la barre de recherche est vide, réinitialiser la liste des patients
      this.getdocument();
      return;
    }

    // Filtrer les patients dont le nom commence par le terme de recherche
    this.documents = this.documents.filter(documents =>
      documents.description.toLowerCase().startsWith(searchTerm)
    );
  }

  async envoyer() {
    if (!this.selectedFile) {
      console.error('Aucun fichier sélectionné.');
      return;
    }

    // Vérifier si la description est vide
    if (!this.documentDescription) {
      const toast = await this.toastController.create({
        message: 'La description est obligatoire.',
        duration: 1500, // Durée du toast en millisecondes
        position: 'top', // Position du toast (peut être 'top', 'bottom', 'middle')
        color: 'danger', // Couleur du toast
      });
      await toast.present();
      return;
    }

    // Générer un ID unique pour le fichier
    const fileId = this.firestore.createId();

    // Chemin de stockage dans Firebase Storage
    const filePath = `documents/${fileId}`;

    try {
      // Télécharger le fichier vers Firebase Storage
const fileRef = this.storage.ref(filePath);
      const uploadTask = this.storage.upload(filePath, this.selectedFile);

      // Attendre la fin du téléchargement
      await uploadTask.then();

      // Récupérer l'URL du fichier téléchargé
      const fileUrl = await fileRef.getDownloadURL().toPromise();

      // Enregistrer les métadonnées dans Firestore
      await this.firestore.collection('documents').doc(fileId).set({
        uid:fileId,
        useruid:patientUidA,
        url: fileUrl,
        description: this.documentDescription,
        // Ajoutez d'autres champs si nécessaire
      });

      // Réinitialiser les valeurs
      this.selectedFile = null;
      this.selectedFileUrl = null;
      this.documentDescription = '';

      // Afficher un message de succès
      const toast = await this.toastController.create({
        message: 'Le document a été enregistré avec succès.',
        duration: 1500, // Durée du toast en millisecondes
        position: 'top', // Position du toast (peut être 'top', 'bottom', 'middle')
        color: 'success', // Couleur du toast
      });
      await toast.present();

      // Actualiser la liste des documents
      this.getdocument();

    } catch (error) {
      console.error('Erreur lors de l\'envoi du fichier :', error);

      // Afficher un message d'erreur
      const toast = await this.toastController.create({
        message: 'Une erreur est survenue lors de l\'envoi du fichier.',
        duration: 1500, // Durée du toast en millisecondes
        position: 'top', // Position du toast (peut être 'top', 'bottom', 'middle')
        color: 'danger', // Couleur du toast
      });
      await toast.present();
    }
  }
}
