import { CommonModule } from '@angular/common';
import { Component, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { IonicModule, ModalController } from '@ionic/angular';
import { Subject, takeUntil } from 'rxjs';
import { PersonneModalComponent } from '../personne-modal/personne-modal.component';
import { RendezvousmodalComponent } from '../rendezvousmodal/rendezvousmodal.component';
import { medecinUid } from 'src/app/recherche-medecin/recherche-medecin.page';
import { RendezVousData, getRdvByMedUid } from 'src/firebaseConfig';



@Component({
  selector: 'app-work-hours-modal',
  templateUrl: './work-hours-modal.component.html',
  styleUrls: ['./work-hours-modal.component.scss'],
})
export class WorkHoursModalComponent implements OnInit {
  rdvs: RendezVousData[] = []; // Type à adapter en fonction de vos données de rendez-vous
  takenHours: string[] = [];
  hoursAvailable: boolean[] = new Array(24).fill(true); // Supposons 24 heures dans une journée

  workHours: string[] = [
    '8H00 - 8H30',
    '8H30 - 9H00',
    '9H00 - 9H30',
    '9H30 - 10H00',
    '10H00 - 10H30',
    '10H30 - 11H00',
    '11H00 - 11H30',
    '11H30 - 12H00',
    '14H00 - 14H30',
    '14H30 - 15H00',
    '15H00 - 15H30',
    '15H30 - 16H00',
    '16H00 - 16H30',
    '16H30 - 17H00',
  ];



  constructor(private modalController: ModalController, private firestore: AngularFirestore) {}

  async ngOnInit() {
    const rdvs = await getRdvByMedUid(this.firestore, medecinUid);
    console.log('rdvs       ',rdvs);
    this.markTakenHours();

  }  

  close() {
    this.modalController.dismiss();
  }
  selectedDate: string = '';
  toggleSelection(hour: string, date: string) {
   selectedHour = hour;
   selectedDate = date;
    console.log('Heure sélectionnée :', selectedHour);
    console.log('Date sélectionnée :', selectedDate);
    console.log('med   ',medecinUid)
  }

  generateDateList(): string[] {
    const dateList: string[] = [];
    const today = new Date();
    for (let i = 0; i < 8; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);

      // Vérifier si la date générée est un samedi ou un dimanche
      if (date.getDay() !== 0 && date.getDay() !== 6) {
        const formattedDate = this.convertDateToString(date);
        dateList.push(formattedDate);

      this.rdvs.forEach(rdv => {
        if (rdv.date === formattedDate && this.workHours.includes(rdv.heure)) {
          const index = this.workHours.indexOf(rdv.heure);
          if (index !== -1) {
            this.workHours[index] = 'Indisponible';
          }
        }
      });
    }
  }
    return dateList;
  }

  convertDateToString(date: Date): string {
    const year = date.getFullYear();
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    return `${year}-${month}-${day}`;
  }


   async openWorkHoursModal() {
    const modal = await this.modalController.create({
      component: PersonneModalComponent,
      cssClass: 'personne-modal',
    });
    return await modal.present();
  }

  async openrdvModal() {
    const modal = await this.modalController.create({
      component: RendezvousmodalComponent,
      cssClass: 'work-hours-modal',
    });
    this.close
    return await modal.present();
  }

  // Définir une liste pour stocker les rendez-vous pris pour une journée donnée

// Fonction appelée lorsque l'utilisateur sélectionne une date
async onDateSelected(date: string) {
  // Load the taken hours for the selected date
  const takenHours = await this.getTakenHoursByDate(this.firestore,medecinUid, date);

  if (takenHours) {
    // Update the work hours array by disabling the taken hours
    this.workHours = this.workHours.map(hour => {
      // Check if the hour is taken for the selected date
      const isTaken = takenHours.includes(hour);
      return isTaken ? hour + ' (Réservé)' : hour;
    });
  }
}
async  getTakenHoursByDate(firestore: AngularFirestore,uid:string, date: string) {
  const query = firestore.collection('medecin').doc(uid).collection('rdv', ref => ref.where('date', '==', date));
  const snapshot = await query.ref.get();
  const takenHours: string[] = [];

  snapshot.forEach(doc => {
    const data = doc.data();
    takenHours.push(data['heure']);
  });

  return takenHours;
}

// Fonction pour vérifier si une heure est déjà prise
isHourTaken(hour: string): boolean {
  return hour.endsWith('(Réservé)');
}
markTakenHours() {
  this.rdvs.forEach(rdv => {
    const hour = rdv.heure; // Supposons que 'heure' est une propriété de rendez-vous contenant l'heure du rendez-vous
    const hourIndex = this.getHourIndex(hour);
    if (hourIndex >= 0) {
      this.hoursAvailable[hourIndex] = false;
    }
  });
}

getHourIndex(hour: string): number {
  // Convertir l'heure au format index (par exemple, '8H00' => 8)
  // Vous devez implémenter cette logique selon le format de vos heures
  return parseInt(hour.substring(0, 2), 10);
}

}


@NgModule({
  declarations: [WorkHoursModalComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule
  ],
  providers: [AngularFirestore],
})
export class WorkHoursModalModule {}

export let selectedHour: string ;
export let selectedDate: string ;
