import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-affiche-patient',
  templateUrl: './affiche-patient.page.html',
  styleUrls: ['./affiche-patient.page.scss'],
})
export class AffichePatientPage implements OnInit {

  constructor() { }

  ngOnInit() {
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title>Liste des rendez-vous</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <ion-item *ngFor="let rdv of rdvs">
      <ion-label>{{ rdv.date }} - {{ rdv.heure }} - {{ rdv.nom }} {{ rdv.prenom }}</ion-label>
      <ion-button (click)="openFichePatientModal(rdv)" color="primary">Fiche Patient</ion-button>
    </ion-item>
  </ion-list>
</ion-content>
