import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { LoadingController, ToastController } from '@ionic/angular';
import { finalize } from 'rxjs/operators';
import { inscritUid } from '../login/login.page';
import { patientUidA } from '../patient/patient.page';
import { getDocuments, getDocumentsforpatient, medecinUid } from 'src/firebaseConfig';

interface DocumentData {
  url: string;
  description: string;
  useruid: string;
}

@Component({
  selector: 'app-document',
  templateUrl: './document.page.html',
  styleUrls: ['./document.page.scss'],
})
export class DocumentPage implements OnInit {
  documents: DocumentData[] = [];
  documentDescription: string = '';
  selectedFile: File | null = null;
  description: any;
  patient: any;
  searchDescription:string='';

  constructor(
    private firestore: AngularFirestore,
    private storage: AngularFireStorage,
    private toastController: ToastController,
    private loadingController: LoadingController
  ) {}

  ngOnInit() {
    this.getDocuments();
  }

  async getDocuments() {
    const documents = await getDocumentsforpatient(this.firestore, inscritUid);
    if (documents) {
      this.documents = documents;
    }
  }



  async searchDocumentsByDescription(event: any) {
  if (!event || !event.target || !event.target.value) {
    console.log('Veuillez entrer une description.');
    return;
  }

  const description = event.target.value;

  try {
    // Filtrer les documents pour la description spécifiée
    const filteredDocuments = this.documents.filter(document => document.description === description);

    if (filteredDocuments.length > 0) {
      console.log('Documents trouvés avec la description', description, ':', filteredDocuments);
      this.documents = filteredDocuments;
    } else {
      console.log('Aucun document trouvé avec la description', description);
    }
  } catch (error) {
    console.error('Erreur lors de la recherche des documents pour la description :', error);
  }
}

async search(event: CustomEvent) {
  const searchTerm = (event.target as HTMLInputElement).value.toLowerCase().trim();
  if (!searchTerm) {
    // Si la barre de recherche est vide, réinitialiser la liste des documents
    this.getDocuments();
    return;
  }

  // Filtrer les documents dont la description commence par le terme de recherche
  this.documents = this.documents.filter(documents =>
    documents.description.toLowerCase().startsWith(searchTerm)
  );
}
async onFileSelected(event: any) {
  const file: File | null = event.target.files[0];
  this.selectedFile = file;

  if (file instanceof File) {
    try {
      // Générer un ID unique pour le fichier
      const fileId = Math.random().toString(36).substring(2);
      const filePath = `documents/${fileId}_${file.name}`;

      // Uploader le fichier dans Firebase Storage
      const fileRef = this.storage.ref(filePath);
      const uploadTask = this.storage.upload(filePath, file);

      // Récupérer l'URL permanente du fichier une fois qu'il est téléchargé
      uploadTask.snapshotChanges().pipe(
        finalize(async () => {
          const fileUrl = await fileRef.getDownloadURL().toPromise();

          // Maintenant, vous pouvez utiliser fileUrl comme lien permanent pour le fichier
          console.log('Lien permanent du fichier :', fileUrl);

          // Enregistrer la description dans une variable
          const description = this.description;

          // Vérifier si la description est fournie
          if (description) {
            // Ajouter le document avec le lien permanent dans Firestore
            const docRef = this.firestore.collection('medecin').doc(medecinUid.uid).collection('documents').doc();
            await docRef.set({
              medecinUid: medecinUid.uid,
              patientUid: patientUidA,
              url: fileUrl,
              description: description
            });
            this.getDocuments();
            console.log('Document ajouté avec succès.');
          } else {
            console.error('La description est obligatoire pour télécharger le fichier.');
          }
        })
      ).subscribe();

    } catch (error) {
      console.error('Erreur lors de l\'ajout du document :', error);
    }
  }
}

async envoyer() {
  if (!this.selectedFile) {
    console.error('Aucun fichier sélectionné.');
    return;
  }

  // Vérifier si la description est vide
  if (!this.description) {

    const toast = await this.toastController.create({
      message: 'La description est obligatoire.',
      duration: 1500, // Durée du toast en millisecondes
      position: 'top', // Position du toast (peut être 'top', 'bottom', 'middle')
      color: 'danger', // Couleur du toast
    });
    await toast.present();
    return;
  }

  // Générer un ID unique pour le fichier
  const fileId = this.firestore.createId();

  // Chemin de stockage dans Firebase Storage
  const filePath = `documents/${fileId}`;

  try {
    // Télécharger le fichier vers Firebase Storage
    const fileRef = this.storage.ref(filePath);
    const uploadTask = this.storage.upload(filePath, this.selectedFile);

    // Attendre la fin du téléchargement
    await uploadTask.then();

    // Récupérer l'URL du fichier téléchargé
    const fileUrl = await fileRef.getDownloadURL().toPromise();

    // Enregistrer les métadonnées dans Firestore
    await this.firestore.collection('documents').doc(fileId).set({
      useruid:this.patient?.useruid,
      url: fileUrl,
      description: this.description,
      // Ajoutez d'autres champs si nécessaire
    });
    const documents = await getDocuments(this.firestore, medecinUid.uid, patientUidA);
    if (documents && documents.length > 0) {
      this.documents = documents;}
    console.log('Fichier téléchargé et enregistré avec succès.');

      const docu = {
        url: fileUrl,
        description: this.description,
        med:medecinUid.uid,
      }
      const medref =await this.firestore.collection('inscrits').doc(this.patient?.useruid).collection('document').add(docu);





  } catch (error) {
    console.error('Erreur lors de l\'envoi du fichier :', error);
  }
  }





}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-button routerLink="/tabs/tab1" fill="clear">Retour</ion-button>
    </ion-buttons>
    <ion-title>Documents</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-card>
  <ion-card-content>
    <ion-card-header>
      <br>
      <ion-searchbar placeholder="Rechercher" ></ion-searchbar>
      <br>
    </ion-card-header>
        <ion-card class="upload-document">
          <ion-card-content>


            <br><br> 
            <br><br> <br><br> <br><br> 
              <input type="file" id="fileInput" name="fileInput" accept="*/*" (change)="onFileSelected($event)">

        </ion-card-content>
        </ion-card>

        <div *ngIf="selectedFile">
          <ion-input type="text" [(ngModel)]="description" placeholder="Enter document description" required></ion-input>
          <ion-button class="ion-text-end" (click)="envoyer()">
            <ion-icon name="send-outline"></ion-icon>
            &nbsp;&nbsp;Envoyer
          </ion-button>

          <img [src]="selectedFile" alt="Selected Image">
        </div>
       <ion-card>
        <div>
          <ion-list>
            <ion-input type="text" [(ngModel)]="searchDescription"placeholder="entrer une description a rechercher" color="primary" ></ion-input>
<ion-button (click)="searchDocumentsByDescription(searchDescription)">Rechercher</ion-button>
            <ion-item *ngFor="let document of documents">
              <img [src]="document.url" width="60" height="60">
              <ion-label>{{ document.description }}</ion-label>
              <a float="right" [href]="document.url"  target="_blank" > Voir détail..</a>
            </ion-item>
            <br><br>
          </ion-list>
        </div>
       </ion-card>
      </ion-card-content>
    </ion-card>
</ion-content>
