import { Component, Input, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ModalController } from '@ionic/angular';
import { getConsultations, getRdvByPatientUid, medecinUid, savePatient } from 'src/firebaseConfig';
import { patient, patientUidA } from '../patient/patient.page';
import { meduid } from '../login-medecin/login-medecin.page';

interface PatientData {
  nom: string;
  prenom: string;
  age?: number;
  adresse?: string;
  telephones?: string;
  uid:string ;
}

interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string; // Ajout de la propriété medecinNom comme optionnelle
  medecinPrenom?: string; 
  medecinuid:string;// Ajout de la propriété medecinPrenom comme optionnelle
  key:string ;
  excuse?:string;
  statut?:string;
}
interface consultationData {
  date: string;
  details?: string;
  uid?: string;
}
@Component({
  selector: 'app-patientpage',
  templateUrl: './patientpage.page.html',
  styleUrls: ['./patientpage.page.scss'],
})


export class PatientpagePage implements OnInit {
  @Input() rendezVousData!: RendezVousData; 
  Segment: string = 'fiches'; 

  fichePatient = {
    nom: '',
    prenom: '',
    age: null,
    adresse: '',
    telephone: ''
    // Ajoutez d'autres champs selon vos besoins
  };
  rdv: RendezVousData[] | null | undefined;
  patient:PatientData =patient ;
  consultations!: consultationData[];

  constructor(private firestore: AngularFirestore,private modalController: ModalController) {
    this.rendezVousData = {} as RendezVousData;
  }
  prenom :string = this.rendezVousData.prenom;
  nom:string=this.rendezVousData.nom;


  async ngOnInit() {
    this.rdv= await getRdvByPatientUid(this.firestore,  patientUidA);
    console.log('patient')
    console.log('patient',patient)
    this.loadConsultations();

    console.log('rdv',this.rdv)

  }
  async loadConsultations() {
    try {
      // Utilisez la fonction getConsultation pour obtenir les consultations
      const consultations = await getConsultations(this.firestore,medecinUid.uid, patientUidA);

      if (consultations && consultations.length > 0) {
        // Assurez-vous que les consultations sont définies et non vides
        this.consultations = consultations;
      } else {
        console.log('Aucune consultation trouvée pour ce patient.');
      }
    } catch (error) {
      console.error('Erreur lors du chargement des consultations du patient :', error);
    }
  }
  closeModal() {
    this.modalController.dismiss(); // Fermer le modal
  }

  async saveFichePatient() {
    try {
     this.fichePatient.nom=this.patient.nom ;
     this.fichePatient.prenom=this.patient.prenom;
     console.log('prenom ',this.fichePatient.prenom,'nom ',this.fichePatient.nom)

      console.log('Données de la fiche patient à sauvegarder :', this.fichePatient);

      // Appel de la fonction savePatient pour sauvegarder le patient chez le médecin
      await savePatient(this.firestore, this.fichePatient, meduid);


    } catch (error) {
      console.error('Erreur lors de l\'ajout du patient chez le médecin :', error);
    }
  }

}

<ion-header>
  <ion-toolbar>
    <ion-title>info patients : {{ rendezVousData.nom }} {{ rendezVousData.prenom }} </ion-title>
  </ion-toolbar>
  <ion-toolbar>
    <ion-segment [(ngModel)]="segment">
      <ion-segment-button value="fiches">Fiche Patient</ion-segment-button>
      <ion-segment-button value="consultations">Consultations</ion-segment-button>
      <ion-segment-button value="documents">Documents</ion-segment-button>
    </ion-segment>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card *ngIf="segment === 'fiches'">
    <ion-card-header>
      Fiches Patients
    </ion-card-header>
    <ion-card-content>
      <!-- Contenu pour les fiches patients -->
      <ion-card>
        <ion-card-header>
          <ion-card-title>{{ rendezVousData.nom }} {{ rendezVousData.prenom }}</ion-card-title>
        </ion-card-header>

        <ion-card-content>
          <!-- Champs du rendez-vous -->
          <ion-item>
            <ion-label>Nom</ion-label>
            <ion-input [(ngModel)]="rendezVousData.nom" disabled></ion-input>
          </ion-item>

          <ion-item>
            <ion-label>Prénom</ion-label>
            <ion-input [(ngModel)]="rendezVousData.prenom" disabled></ion-input>
          </ion-item>

          <!-- Champs classiques d'une fiche patient -->
          <ion-item>
            <ion-label>Âge</ion-label>
            <ion-input type="number" [(ngModel)]="fichePatient.age"></ion-input>
          </ion-item>

          <ion-item>
            <ion-label>Adresse</ion-label>
            <ion-input [(ngModel)]="fichePatient.adresse"></ion-input>
          </ion-item>

          <ion-item>
            <ion-label>Téléphone</ion-label>
            <ion-input type="tel" [(ngModel)]="fichePatient.telephone"></ion-input>
          </ion-item>

          <!-- Ajoutez d'autres champs selon vos besoins -->

          <ion-button expand="block" (click)="saveFichePatient()">Enregistrer</ion-button>
        </ion-card-content>
      </ion-card>
    </ion-card-content>
  </ion-card>
  <ion-card *ngIf="segment === 'consultations'">
    <ion-content>
      <ion-list>
        <ion-item *ngFor="let consultation of consultations">
          <ion-label>{{ consultation.date }}</ion-label>
          <ion-button  color="primary">Modifier</ion-button>
        </ion-item>
      </ion-list>
    </ion-content>
    <ion-card *ngIf="segment === 'documents'">
