import { CommonModule } from '@angular/common';
import { Component, Input, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';
import { IonicModule, ModalController } from '@ionic/angular';
import { medecinUid, modifierSecretaire } from 'src/firebaseConfig';
import { uida } from '../parametre-secretaire.page';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss'],
})
export class FormComponent  implements OnInit {

  @Input() secretaireID!: string;
  myForm: FormGroup = this.fb.group({
    prenom: ['', Validators.required],
    nom: ['', Validators.required],
    numero: ['', Validators.required],
    gmail: ['', [Validators.required, Validators.email]],
    password: ['', Validators.required],
  });

  constructor(private modalController: ModalController, private firestore: AngularFirestore, private fb: FormBuilder) {}
  ngOnInit() {

  console.log('uid   ', uida);
  }

  async onSubmit() {
    const medecinId = medecinUid.uid;

    if (this.myForm.valid) {
      await this.modifierSecretaire();
      await this.modalController.dismiss();
    }
  }

  async modifierSecretaire() {
    const nom = this.myForm.get('nom')?.value;
    const prenom = this.myForm.get('prenom')?.value;
    const mail = this.myForm.get('gmail')?.value;
    const numero = this.myForm.get('numero')?.value;
    const password = this.myForm.get('password')?.value;
    const medecinUid = 'meduid';

    try {
      await modifierSecretaire(this.firestore, this.secretaireID,nom, prenom, mail, numero, password, uida);
      console.log('Secrétaire enregistrée avec succès.');
    } catch (error) {
      console.error('Erreur lors de l\'enregistrement du secrétaire :', error);
      // Gérez les erreurs ici
    }
  }

  async fermer() {
    await this.modalController.dismiss(); 
  }
}
@NgModule({
  declarations: [FormComponent],
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    IonicModule,
  ],
  exports: [FormComponent],
  providers: [AngularFirestore],
})
export class FormModule {}

<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Formulaire</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="fermer()">
        <ion-icon name="close"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <form [formGroup]="myForm" >
    <!-- Champs Prénom -->
    <ion-item>

      <ion-input formControlName="prenom" label="Prénom"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="nom" label="Nom"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="numero" label="Numéro"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="gmail" label="Gmail"></ion-input>
    </ion-item>

    <ion-item>

      <ion-input formControlName="password" type="password" label="Password"></ion-input>
    </ion-item>




    <ion-button expand="full" type="submit" (click)="Modifiersecretaire()">Soumettre</ion-button>

  </form>
</ion-content>
