import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import {  ToastController } from '@ionic/angular';
import { loginMedecin, medecinUid } from 'src/firebaseConfig';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-medecin',
  templateUrl: './login-medecin.page.html',
  styleUrls: ['./login-medecin.page.scss'],
})

export class LoginMedecinPage implements OnInit {

  email: string = '';
  password: string = '';
  navCtrl: any;


  constructor(private toastController: ToastController, private firestore: AngularFirestore, private router: Router) { }


  async onLoginMedecin() {
    try {
      const result = await loginMedecin(this.firestore, this.email, this.password);
      if (result) {
        const { uid } = result;
        console.log('Login successful! Navigating to homemedecin page...');
        console.log('Medecin UID:', uid);

        // Affectez la valeur de l'UID à la variable exportable medecinUid
        medecinUid.uid = uid;

        this.router.navigate(['/app-medecin']);
      } else {
        console.log('Login failed. Handle failure actions.');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion du médecin :', error);
    }
  }


  loginUser(){

  }

  ngOnInit() {
  }

}

<ion-header>
  <ion-toolbar>

    <ion-button slot="start" (click)="back()">
      Retour
    </ion-button>

    <ion-title >Connexion</ion-title>
  </ion-toolbar>
</ion-header>
  <ion-content>

    <ion-grid>
      <ion-row >
        <ion-col >
          <div class="ion-text-center">
            <br><br><br>
            <h4 class="warning">Login Form Medecin</h4>
          </div>
          <br><br><br><br><br><br>
          <div class="form">
            <ion-item>
              <ion-input placeholder="Enter email" type="email" [(ngModel)]="email">

                <ion-icon name="mail-outline">
                </ion-icon>
              </ion-input>
            </ion-item>
            <br>
            <form>
            <ion-item>
              <ion-input placeholder="Enter password" type="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}">

                <ion-icon name="lock-closed-outline"></ion-icon>
              </ion-input>
            </ion-item>
          </form>
            <br>
          </div>
          <div>

            <ion-button (click)="onLoginMedecin()" size="large" expand="block" shape="round" color="success">Login</ion-button>
            <ion-button routerLink="/forgotpass" fill="clear" class="danger">
              Forgot Password
            </ion-button>

          </div>
        </ion-col>
      </ion-row>
    </ion-grid>
  </ion-content>
