import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getMedecinsByLocalisationAndSpecialite } from 'src/firebaseConfig';
import { NavController } from '@ionic/angular';

// Interface pour décrire le format des données des médecins
interface MedecinData {
  uid: string;
  nom: string;
  prenom: string;
  localisation: string;
  specialite: string;
  // ... autres champs si nécessaire
}

@Component({
  selector: 'app-recherche-medecin',
  templateUrl: './recherche-medecin.page.html',
  styleUrls: ['./recherche-medecin.page.scss'],
})
export class RechercheMedecinPage implements OnInit {

  medecins: MedecinData[] = [];
  etat: string = '';
  specialite: string | null = null;


  specialites: string[] = [
    'Médecine générale',
    'Cardiologie',
    'Chirurgie générale',
    'Pédiatrie',
    'Gynécologie obstétrique',
    'Radiologie',
    'Médecine interne',
    'Orthopédie',
    'Ophtalmologie',
    'Dermatologie',
    'Anesthésiologie',
    'Neurologie',
    'Gastro-entérologie',
    'Urologie',
    'Oncologie',
    'Médecine d\'urgence',
    'Pneumologie',
    'Endocrinologie',
    'Néphrologie',
    'Rhumatologie',
    'Psychiatrie',
    'Médecine du travail',
    'Pédiatrie néonatale',
    'Infectiologie',
    'Hématologie',
    'Pédiatrie médicale',
    'Médecine de réadaptation',
    'Médecine du sport',
    'Pédiatrie chirurgicale',
    'Chirurgie orthopédique',
    'Chirurgie cardiovasculaire',
    'Chirurgie plastique et reconstructive',
    'Médecine nucléaire',
    'Allergologie',
    'Pédiatrie sociale',
    'Chirurgie vasculaire',
    'Médecine légale',
    'Gériatrie',
    'Médecine de famille',
    'Hépato-gastro-entérologie',
    'Médecine tropicale',
    'Chirurgie maxillo-faciale',
    'Médecine de la douleur',
    'Médecine palliative',
    'Endocrinologie pédiatrique',
    'Néonatologie',
    'Chirurgie thoracique',
    'Chirurgie abdominale',
    'Néonatalogie',
    'Immunologie'
  ];


  constructor(private firestore: AngularFirestore, private navCtrl: NavController) { }

  ngOnInit() {
    this.fetchMedecins();
  }

  async fetchMedecins() {
    try {
      if (this.etat && this.specialite) {
        this.medecins = await getMedecinsByLocalisationAndSpecialite(this.firestore, this.etat, this.specialite);
        console.log('Médecins disponibles :', this.medecins);
      } else {
        console.error('Paramètres de recherche non valides.');
        // Ajoutez une logique pour afficher un message à l'utilisateur, si nécessaire
        // Par exemple, utilisez une alerte ou un autre mécanisme pour informer l'utilisateur
        // d'entrer des paramètres valides pour la recherche.
      }
    } catch (error) {
      console.error('Erreur lors de la récupération des médecins (fetchMedecins):', error);
      // Ajoutez une logique pour afficher un message à l'utilisateur en cas d'erreur
      // Par exemple, utilisez une alerte ou un autre mécanisme pour informer l'utilisateur
      // qu'une erreur s'est produite lors de la récupération des médecins.
    }
  }

  goToAfficheMedecin(medecinUidValue: string) {
    // Stockez le medecinUid dans la variable exportée
    medecinUid = medecinUidValue;
    console.log('uid envoyé est :',medecinUidValue);

    // Utilisez this.navCtrl pour naviguer vers la page AfficheMedecinPage
    this.navCtrl.navigateForward('/affiche-medecin');
  }

}
export let medecinUid: string;

<ion-header [translucent]="true" >
  <ion-toolbar>
    <ion-title>Recherche Medecin</ion-title>
    <ion-button slot="start" routerLink="/tabs/tab2" >
      Retour
    </ion-button>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-grid>
    <ion-row>
      <ion-col>
  <ion-item  class="entier">
    <ion-label position="stacked"><ion-icon name="search-outline" class="warning"></ion-icon>&nbsp;Spécialité</ion-label>
    <ion-select [(ngModel)]="specialite" name="specialite">
      <ion-select-option *ngFor="let specialite of specialites" [value]="specialite">{{ specialite }}</ion-select-option>
    </ion-select>
  </ion-item>
</ion-col>
<ion-col>
  <ion-item>
    <label for="etats_tunisie">Sélectionnez un État</label>
    <select id="etats_tunisie" name="etats_tunisie" [(ngModel)]="etat">
        <option value="Ariana">Ariana</option>
        <option value="Beja">Béja</option>
        <option value="Ben Arous">Ben Arous</option>
        <option value="Bizerte">Bizerte</option>
        <option value="Gabes">Gabès</option>
        <option value="Gafsa">Gafsa</option>
        <option value="Jendouba">Jendouba</option>
        <option value="Kairouan">Kairouan</option>
        <option value="Kasserine">Kasserine</option>
        <option value="Kebili">Kébili</option>
        <option value="Le Kef">Le Kef</option>
        <option value="Mahdia">Mahdia</option>
        <option value="La Manouba">La Manouba</option>
        <option value="Médenine">Médenine</option>
        <option value="Monastir">Monastir</option>
        <option value="Nabeul">Nabeul</option>
        <option value="Sfax">Sfax</option>
        <option value="Sidi Bouzid">Sidi Bouzid</option>
        <option value="Siliana">Siliana</option>
        <option value="Sousse">Sousse</option>
        <option value="Tataouine">Tataouine</option>
        <option value="Tozeur">Tozeur</option>
        <option value="Tunis">Tunis</option>
        <option value="Zaghouan">Zaghouan</option>
    </select>
    </ion-item>
</ion-col>
  <ion-col size="auto">
    <ion-button  color="danger" (click)="fetchMedecins()"  >
      <ion-icon name="search-outline"></ion-icon>
    </ion-button>
  </ion-col>
</ion-row>
</ion-grid>
<ion-card *ngFor="let medecin of medecins">
  <ion-card-header>
    {{ medecin.nom }} {{ medecin.prenom }}
  </ion-card-header>
  <ion-card-content>
    <p>Localisation: {{ medecin.localisation }}</p>
    <p>Spécialité: {{ medecin.specialite }}</p>
    <p>Uid: {{ medecin.uid }}</p>
    <ion-button (click)="goToAfficheMedecin(medecin.uid)" color="success">Voir détails</ion-button>

  </ion-card-content>
</ion-card>
</ion-content>
