import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getConsultations, getPatientsByMedUid } from 'src/firebaseConfig';
import { medecinUid } from 'src/firebaseConfig';

interface consultationData {
  date: string;
  details?: string;
}

interface PatientData {
  uid: string;
  nom: string;
  prenom: string;
  age: number;
  sexe: string;
  groupeSanguin: string;
  situationFamiliale: string;
  profession: string;
  email: string;
  telephone: string;
  telephone2: string;
  numeroAssurance: string;
  adresse: string;
  note: string;
  antecedentsMedicaux: string;
  antecedentsChirurgicaux: string;
  antecedentsFamiliaux: string;
  autresAntecedents: string;
  medicaments: string;
  useruid: string;
  consultations?: consultationData[];
}

@Component({
  selector: 'app-consultation-med',
  templateUrl: './consultation-med.page.html',
  styleUrls: ['./consultation-med.page.scss'],
})
export class ConsultationMedPage implements OnInit {
  consultationDate: string = ''; // Variable pour stocker la date de consultation
  consultations: consultationData[] = [];
  uid: string = medecinUid.uid;
  patients: PatientData[] = [];

  constructor(private firestore: AngularFirestore) {}

  async ngOnInit() {
    console.log('Med UID:', this.uid);
    await this.getPatients();
  }

  async searchConsultations() {
    try {
      if (this.consultationDate) {
        // Effectuez la recherche des consultations pour la date sélectionnée
        const consultations = await getConsultationsForDate(this.firestore, medecinUid.uid, this.consultationDate);
        console.log('Consultations for date', this.consultationDate, ':', consultations);

        if (consultations && consultations.length > 0) {
          this.consultations = consultations;
        } else {
          console.log('No consultations found for this date.');
          this.consultations = []; // Réinitialisez la liste des consultations
        }
      } else {
        console.log('Veuillez sélectionner une date de consultation.');
      }
    } catch (error) {
      console.error('Error loading consultations for date:', error);
    }
  }

  async getPatients() {
    try {
      const patients = await getPatientsByMedUid(this.firestore, this.uid);
      console.log(patients);
      if (patients.length > 0) {
        this.patients = patients.map(patient => ({
          ...patient,
          consultations: [] // Initialiser consultations comme un tableau vide
        }));
        console.log('patients', this.patients);

        // Charger les consultations pour chaque patient
        for (const patient of this.patients) {
          const consultations = await getConsultations(this.firestore, medecinUid.uid, patient.uid);
          if (consultations && consultations.length > 0) {
            patient.consultations = consultations; // Ajouter les consultations au patient
          }
        }
      }
      console.log("Patients trouvés :", this.patients);
    } catch (error) {
      console.error('Erreur lors de la récupération des patients :', error);
    }
  }
}

async function getConsultationsForDate(firestore: AngularFirestore, medecinUid: string, date: string) {
  try {
    const consultationsSnapshot = await firestore
      .collection('medecin')
      .doc(medecinUid)
      .collection('consultations')
      .where('date', '==', date)
      .get()
      .toPromise();

    if (consultationsSnapshot) {
      return consultationsSnapshot.docs.map(doc => doc.data() as consultationData);
    } else {
      return []; // Aucune consultation trouvée
    }
  } catch (error) {
    console.error('Error fetching consultations for date:', error);
    throw error;
  }
}
<ion-header [translucent]="true">

  <ion-toolbar>
    <ion-button routerLink="/app-medecin" slot="start" fill="clear">retour</ion-button>
    <ion-title>Consultations</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header>
    <br>
    <form (ngSubmit)="searchConsultations()" #searchForm="ngForm">
      <ion-item>
        <ion-label>Date de consultation</ion-label>
        <ion-input type="date" name="consultationDate" [(ngModel)]="consultationDate" required></ion-input>
      </ion-item>
      <ion-button type="submit" [disabled]="!searchForm.valid">Rechercher</ion-button>
    </form>
    <br>
  </ion-header>
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Consultations</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-list>
    <ion-item *ngFor="let patient of patients">
      <ion-card class="rempli">
        <ion-card-header>
          <ion-card-title><b>Info patient {{ patient.prenom }} {{ patient.nom }} </b></ion-card-title>
        </ion-card-header>
        <ion-card-content>
          <ion-list>
            <ion-item *ngFor="let consultation of patient.consultations">
              {{ consultation.date }}
            </ion-item>
          </ion-list>
        </ion-card-content>
      </ion-card>     
    </ion-item>
  </ion-list>

</ion-content>
